import "@/styles/index.css";  // <â€” must be FIRST

// import "./dev/tap";
// Removed mountEnhancer AND dev/index - asset cards now use proper routing
import React from "react";
import ReactDOM from "react-dom/client";
import { Router } from "wouter";
import { QueryClientProvider } from "@tanstack/react-query";
import { queryClient } from "@/lib/queryClient";
import EccErrorBoundary from "./components/EccErrorBoundary";
import DevDiagBar from "./components/DevDiagBar";
import App from "./App";

if (import.meta.env.DEV) {
  // React StrictMode + aborted fetches during HMR
  window.addEventListener("unhandledrejection", (e: any) => {
    if (e?.reason?.name === "AbortError") e.preventDefault();
  });
  window.addEventListener("error", (e: any) => {
    if (String(e?.message || "").includes("AbortError")) e.preventDefault();
  });
}

const Mode: React.ComponentType<any> = import.meta.env.DEV ? React.Fragment : React.StrictMode;

ReactDOM.createRoot(document.getElementById("root")!).render(
  <Mode>
    <EccErrorBoundary>
      {import.meta.env.DEV && <DevDiagBar />}
      <QueryClientProvider client={queryClient}>
        <Router>
          <App />
        </Router>
      </QueryClientProvider>
    </EccErrorBoundary>
  </Mode>
);

src/components/ErrorBoundary.tsx.content:
import { Component, ReactNode } from "react";

export class ErrorBoundary extends Component<{ children: ReactNode }, { hasError: boolean; msg?: string }> {
  constructor(props:any){ super(props); this.state={ hasError:false, msg: undefined }; }
  static getDerivedStateFromError(err: any){ return { hasError:true, msg:String(err?.message||err) }; }
  componentDidCatch(err:any){ console.error("[CardError]", err); }
  render(){
    return this.state.hasError
      ? <div data-testid="contract-error" className="p-3 border border-rose-700 bg-rose-900/30 rounded">
          <div className="font-semibold">Contract violation</div>
          <div className="text-sm opacity-80">{this.state.msg}</div>
        </div>
      : this.props.children;
  }
}

export default ErrorBoundary;

