  color: var(--bg);
}

/* Main content area adjustment */
.layout {
  margin-left: 280px;
  min-height: 100vh;
  padding: 24px;
  background: var(--bg);
}

/* Responsive behavior */
@media (max-width: 768px) {
  .sidebar {
    transform: translateX(-100%);
    transition: transform 0.3s ease;
  }

  .sidebar.open {
    transform: translateX(0);
  }

