src/styles/_ecc-override.css.content:
:root{
  --ecc-sidebar-w: 280px;
  --ecc-sidebar-w-collapsed: 76px;
  --ecc-bg:#0e0f11; --ecc-panel:#121316; --ecc-seam:#222428;
  --ecc-text:#d7d9dd; --ecc-dim:#a6a8ac; --ecc-gold:#ffd400;
}

/* Reserve rail space globally */
html[data-sidebar-mounted="1"] body{
  padding-left:var(--ecc-sidepad, var(--ecc-sidebar-w)) !important;
  box-sizing:border-box;
}

.sidebar{
  position:fixed; top:0; left:0; height:100vh; z-index:50;
  width:var(--ecc-sidebar-w);
  background:var(--ecc-panel);
  color:var(--ecc-text);
  display:flex; flex-direction:column;
  border-right:1px solid var(--ecc-seam);
}
.sidebar.collapsed{ width:var(--ecc-sidebar-w-collapsed); }

.sidebar .brand{ display:flex; align-items:center; gap:10px; padding:14px 12px 8px; }
.sidebar .brand-logo{ height:42px; width:auto; }
.sidebar .pinBtn{ margin-left:auto; font-size:12px; border:1px solid var(--ecc-seam); background:transparent; color:var(--ecc-dim); border-radius:10px; padding:4px 10px; }
.sidebar.collapsed .pinBtn{ font-size:10px; padding:2px 8px; }

.sidebar .sidebar-scroll{
  height:calc(100vh - 70px);
  overflow-y:auto; -webkit-overflow-scrolling:touch; overscroll-behavior:contain;
  scrollbar-width:none;
}
.sidebar .sidebar-scroll::-webkit-scrollbar{ width:0; height:0; }

.sidebar .section{ padding:6px 8px; }
.sidebar .section-title{ color:var(--ecc-dim); font-size:12px; letter-spacing:.08em; padding:8px 10px 6px; }

.sidebar .nav-row{
  display:grid; grid-template-columns:18px minmax(0,1fr) auto;
  align-items:center; column-gap:10px; padding:8px 10px;
  border-radius:10px; text-decoration:none; color:inherit;
}
.sidebar .nav-row:hover{ background:#17181b; }
.sidebar .leaf.active{ background:#17181b; box-shadow:inset 0 0 0 2px var(--ecc-gold); }
.sidebar .icon{ width:18px; min-width:18px; text-align:center; color:var(--ecc-gold); }
.sidebar .label{ white-space:nowrap; overflow:hidden; text-overflow:ellipsis; }
.sidebar .expand{ opacity:.5; }

.sidebar .group-row{ width:100%; text-align:left; background:transparent; border:0; color:inherit; }
.sidebar .leafList{ padding-left:20px; }

.sidebar.collapsed .nav-row{ grid-template-columns:18px; justify-items:center; padding-left:10px; padding-right:8px; }
.sidebar.collapsed .label,
.sidebar.collapsed .expand,
.sidebar.collapsed .section-title{ display:none; }
.sidebar.collapsed .leafList{ display:none; }

/* Kill any non-canonical rails */
.sidebar:not([data-ecc="primary"]) { display:none !important; visibility:hidden !important; }

/* Main content padding */
.ecc-main{ padding:24px; }

src/styles/app.css.content:
/* =========================================================
   ECC â€” App Shell + Sidebar + Flyout
   ========================================================= */

/* Import Genesis Design System */
@import "./genesis.css";

/* Tailwind CSS utilities for newer components */
@layer utilities {
  .text-xs { font-size: 0.75rem; line-height: 1rem; }
  .text-sm { font-size: 0.875rem; line-height: 1.25rem; }
  .text-xl { font-size: 1.25rem; line-height: 1.75rem; }
  .text-2xl { font-size: 1.5rem; line-height: 2rem; }
  .text-3xl { font-size: 1.875rem; line-height: 2.25rem; }
  .font-bold { font-weight: 700; }
  .font-semibold { font-weight: 600; }
  .font-extrabold { font-weight: 800; }
  .opacity-70 { opacity: 0.7; }
  .opacity-80 { opacity: 0.8; }
  .opacity-90 { opacity: 0.9; }
  .rounded { border-radius: 0.25rem; }
  .rounded-xl { border-radius: 0.75rem; }
  .rounded-2xl { border-radius: 1rem; }
  .p-2 { padding: 0.5rem; }
  .p-3 { padding: 0.75rem; }
  .p-4 { padding: 1rem; }
  .px-3 { padding-left: 0.75rem; padding-right: 0.75rem; }
  .py-2 { padding-top: 0.5rem; padding-bottom: 0.5rem; }
  .mb-1 { margin-bottom: 0.25rem; }
  .mt-2 { margin-top: 0.5rem; }
  .gap-2 { gap: 0.5rem; }
  .gap-3 { gap: 0.75rem; }
  .space-y-2 > * + * { margin-top: 0.5rem; }
  .space-y-4 > * + * { margin-top: 1rem; }
  .grid { display: grid; }
  .flex { display: flex; }
  .items-baseline { align-items: baseline; }
  .justify-between { justify-content: space-between; }
  .flex-wrap { flex-wrap: wrap; }
  .w-full { width: 100%; }
  .border { border-width: 1px; border-color: rgb(229 231 235); }
  .bg-black { background-color: rgb(0 0 0); }
  .bg-gray-200 { background-color: rgb(229 231 235); }
  .bg-yellow-400 { background-color: rgb(250 204 21); }
  .bg-green-600 { background-color: rgb(22 163 74); }
  .bg-neutral-900 { background-color: rgb(23 23 23); }
  .text-white { color: rgb(255 255 255); }
  .text-black { color: rgb(0 0 0); }
  .text-red-600 { color: rgb(220 38 38); }
  .border-neutral-200 { border-color: rgb(229 229 229); }
}

@media (min-width: 768px) {
  .md\:grid-cols-2 { grid-template-columns: repeat(2, minmax(0, 1fr)); }
  .md\:grid-cols-3 { grid-template-columns: repeat(3, minmax(0, 1fr)); }
}

.dark .border-neutral-800 { border-color: rgb(38 38 38); }

/* Base */
*,
*::before,
*::after { box-sizing: border-box; }
* { margin: 0; }
html, body, #root { height: 100%; }
a { color: inherit; text-decoration: none; }
button { background: transparent; border: 0; padding: 0; cursor: pointer; color: inherit; font: inherit; }
:focus-visible { box-shadow: var(--focus); }

/* Shell */
.ecc-shell { display:flex; height:100dvh; width:100%; background:var(--bg); color:var(--text); }
.ecc-main { flex:1 1 auto; min-width:0; overflow:auto; padding:16px 18px; background:var(--bg); position:relative; z-index:1; }
.ecc-page-title { font-size:22px; font-weight:900; letter-spacing:.2px; margin:8px 0 12px; }

/* Tokens used here */
:root{
  --rail-collapsed: 64px;
  --rail-expanded: 320px;
  --z-sidebar: 9000;
  --z-flyout: 10000;

  --nav-bg: var(--surface);
  --nav-fg: var(--text);
  --nav-active: var(--gold);

  --hover-bg: rgba(247, 201, 72, 0.12);
  --sub-hover-bg: rgba(247, 201, 72, 0.08);
}

/* Sidebar */
.ecc-sidebar{
  width:var(--rail-expanded);
  background:var(--nav-bg);
  border-right:1px solid var(--border);
  color:var(--nav-fg);
  transform:translateZ(0);
  z-index:var(--z-sidebar);
  display:flex; flex-direction:column;
}
.ecc-sidebar.is-collapsed{ width:var(--rail-collapsed); }

/* Header (logo only) */
.ecc-header{
  position:sticky; top:0;
  display:flex; align-items:center;
  gap:8px;
  height:56px; padding:0 8px;
  border-bottom:1px solid var(--border);
  background:var(--surface);
  z-index:calc(var(--z-sidebar) + 1);
}
.ecc-logo{
  width:28px; height:28px; border-radius:6px;
  background:var(--gold); color:#0b0f14;
  display:grid; place-items:center; font-weight:700; font-size:12px;
}
.ecc-logo-img{ width:28px; height:28px; object-fit:contain; filter:brightness(1.2) contrast(1.05); }
.ecc-logo-wide{ height:24px; object-fit:contain; filter:brightness(1.2) contrast(1.05); }
.ecc-title{ font-size:13px; font-weight:600; }

/* Footer (PIN lives here) */
.ecc-footer{
  position:sticky; bottom:0;
  margin-top:auto;
  padding:8px;
  border-top:1px solid var(--border);
  background:var(--surface);
  display:flex; justify-content:center; align-items:center;
}
.ecc-pin{
  display:inline-grid; grid-auto-flow:column; align-items:center; gap:8px;
  min-width:44px; height:36px; padding:0 12px;
  border-radius:10px; border:1px solid var(--border);
  background:rgba(247,201,72,0.08); color:var(--text);
  transition:background 150ms var(--ease), border-color 120ms var(--ease), transform 120ms var(--ease);
}
.ecc-pin:hover{ background:rgba(247,201,72,0.22); border-color:var(--gold); transform:translateY(-1px); }
.ecc-pin.is-on{ background:var(--gold); color:#0b0f14; border-color:var(--gold); }
.ecc-pin.is-on:hover{ background:#E2B33C; border-color:#E2B33C; }
.ecc-pin-label{ font-size:12px; font-weight:700; }

.ecc-scroll{ flex:1 1 auto; overflow-y:auto; overflow-x:hidden; padding:8px 6px 10px; }
.ecc-scroll::-webkit-scrollbar{ width:8px; }
.ecc-scroll::-webkit-scrollbar-thumb{ background:transparent; }
.ecc-scroll:hover::-webkit-scrollbar-thumb{ background:var(--border); }

/* Rows */
.ecc-row{ position:relative; margin:2px 4px; border-radius:10px; }
.ecc-row-inner{
  display:grid; grid-template-columns:40px 1fr auto;
  align-items:center; min-height:40px; padding:4px 8px; border-radius:10px;
}
.ecc-row:hover .ecc-row-inner{ background:var(--hover-bg); }
.ecc-row.is-active .ecc-icon{ color:var(--nav-active); }
.ecc-row.is-active .ecc-label{ color:var(--text); font-weight:600; }

.ecc-icon-wrap{ display:grid; place-items:center; color:var(--nav-active); }
.ecc-icon{ display:block; }

.ecc-label{
  display:inline-flex; align-items:center;
  overflow:hidden; text-overflow:ellipsis; white-space:nowrap;
  color:var(--text-muted); padding-right:8px;
}
.ecc-label:hover{ color:var(--text); }
.ecc-caret{ justify-self:end; color:var(--text-subtle); display:grid; place-items:center; }
.ecc-spacer{ flex:1 1 auto; }
.ecc-badge{
  display:inline-grid; place-items:center;
  min-width:20px; height:20px; padding:0 6px;
  font-size:11px; line-height:1; color:#0B0F14;
  background:var(--nav-active); border-radius:999px;
}
.ecc-badge.small{ min-width:18px; height:18px; font-size:10px; }

/* Children */
.ecc-children{ padding:4px 8px 8px 40px; display:grid; gap:4px; }
.ecc-child{
  display:grid; grid-template-columns:16px 1fr auto; align-items:center;
  min-height:28px; column-gap:8px; border-radius:8px; padding:2px 6px; color:var(--text-muted);
}
.ecc-child:hover{ background:var(--sub-hover-bg); color:var(--text); }
.ecc-child.is-active{ color:var(--text); font-weight:600; }
.ecc-child-icon{ color:var(--text-subtle); }
.ecc-child-label{ overflow:hidden; text-overflow:ellipsis; white-space:nowrap; }

/* Collapsed hides labels/children */
.ecc-sidebar.is-collapsed .ecc-title,
.ecc-sidebar.is-collapsed .ecc-label,
.ecc-sidebar.is-collapsed .ecc-children,
.ecc-sidebar.is-collapsed .ecc-badge:not(.small){ display:none; }

/* Flyout (No Fixed Position) */
.ecc-flyout{
  max-height:calc(100dvh - 16px); overflow:auto;
  width:288px; padding:8px; background:var(--surface); border:1px solid var(--border);
  border-radius:12px; box-shadow:var(--shadow-2); pointer-events:auto;
}
.ecc-flyout::-webkit-scrollbar{ width:8px; }
.ecc-flyout::-webkit-scrollbar-thumb{ background:var(--border); }
.ecc-flyout-head{
  display:grid; grid-template-columns:16px 1fr; gap:8px; align-items:center;
  padding:6px 8px 8px; border-bottom:1px solid var(--border); margin:-8px -8px 8px -8px;
}
.ecc-flyout-head-icon{ color:var(--nav-active); }
.ecc-flyout-head-title{ font-weight:700; overflow:hidden; text-overflow:ellipsis; white-space:nowrap; }
.ecc-flyout-item{
  display:grid; grid-template-columns:16px 1fr auto; align-items:center; column-gap:8px;
  min-height:36px; padding:6px 8px; border-radius:8px; color:var(--text-muted);
}
.ecc-flyout-item:hover{ background:var(--hover-bg); color:var(--text); }
.ecc-flyout-icon{ color:var(--text-subtle); }
.ecc-flyout-label{ overflow:hidden; text-overflow:ellipsis; white-space:nowrap; }

/* Panels / Tables */
.panel{ background:var(--surface); border:1px solid var(--border); border-radius:14px; box-shadow:var(--shadow-1); }
.table{ width:100%; background:var(--surface); border:1px solid var(--border); border-radius:12px; border-collapse:separate; border-spacing:0; }
.table thead th{ text-align:left; font-weight:600; color:var(--text-subtle); padding:10px 12px; border-bottom:1px solid var(--border); }
.table tbody td{ padding:10px 12px; color:var(--text); border-top:1px solid var(--border); }
.table tbody tr:hover{ background: rgba(247,201,72,0.06); }

/* ====== ECC portfolio page scaffolding ====== */
.ecc-page { padding: 16px; }
.ecc-page-head {
  display: flex; align-items: center; justify-content: space-between; gap: 12px; margin-bottom: 12px;
}
.ecc-page-title { margin: 0; font-size: 20px; font-weight: 900; color: var(--text); }
.ecc-page-tools { display: flex; align-items: center; gap: 8px; }

.ecc-search {
  width: 280px; max-width: 42vw;
  background: var(--surface); color: var(--text);
  border: 1px solid var(--border); border-radius: var(--radius-md);
  padding: 8px 10px;
}
.ecc-search::placeholder { color: var(--muted-1, #98a2b3); }

.ecc-btn { padding: 8px 12px; border-radius: var(--radius-md); border: 1px solid var(--border); background: var(--surface); color: var(--text); }
.ecc-btn-gold { background: var(--gold); border-color: var(--gold); color: #0b0f14; font-weight: 700; }

.ecc-table-wrap { border: 1px solid var(--border); border-radius: var(--radius-lg); overflow: hidden; background: #0b0f14; }
.ecc-table { width: 100%; border-collapse: collapse; }
.ecc-table th {
  text-align: left; font-weight: 800; color: var(--text);
  padding: 10px; border-bottom: 1px solid var(--border); background: color-mix(in srgb, var(--gold) 6%, var(--surface));
}
.ecc-table td { padding: 10px; border-bottom: 1px solid var(--border); color: var(--text); }
.ecc-table tr:hover td { background: color-mix(in srgb, var(--gold) 5%, var(--surface)); }
.ecc-table-empty { text-align: center; color: var(--muted-1, #98a2b3); padding: 26px 10px; }

/* ==== Keep main content beside the fixed sidebar ==== */

/* Sidebar (No Fixed Position) */
.ecc-sidebar {
  width: var(--rail-expanded);  /* 320px */
  float: left;
}
.ecc-sidebar.is-collapsed { width: var(--rail-collapsed); } /* 64px */

/* Main gets a left margin equal to the current rail width */
.ecc-main {
  margin-left: var(--rail-expanded);
  min-height: 100vh;
  display: block;
}

/* When the rail collapses, shrink the offset */
.ecc-sidebar.is-collapsed + .ecc-main { margin-left: var(--rail-collapsed); }

/* Safety: if anything wraps .ecc-main later, keep the offset using general sibling */
.ecc-sidebar.is-collapsed ~ .ecc-main { margin-left: var(--rail-collapsed); }

/* Optional: small screens default to collapsed offset */
@media (max-width: 1024px) {
  .ecc-main { margin-left: var(--rail-collapsed); }
}

/* CSS safety (ensure no element blocks pointer events on the row) */
.ecc-row * { pointer-events: auto; }

src/styles/theme.css.content:
/* ===== ECC_STYLE_CONTRACT::THEME_TOKENS (DO NOT REMOVE) ===== */
:root{
  /* Brand (Altus) */
  --altus-black: #0B0E12;
  --altus-gold:  #FFD700;   /* primary accent */
  --altus-green: #10B981;   /* positive */
  --altus-red:   #EF4444;   /* negative */
  /* Absolutely NO orange anywhere. */

  /* Text */
  --text-strong:  #E9EDF4;
  --text-normal:  #C2C7D0;
  --text-muted:   #8C93A1;

  /* Surfaces */
  --surface-app:     #0F141B;
  --surface-panel:   #161C24;   /* background behind cards */
  --surface-object:  #2A2F38;   /* light-grey inner box per design contract */
  --surface-border:  rgba(255,255,255,0.06);

  /* Radius/Spacing */
  --radius-lg: 16px;
  --pad-lg:    16px;

  /* Motion */
  --ease-std: cubic-bezier(.2,.8,.2,1);

  /* Genesis compatibility aliases - keeping existing for backward compatibility */
  --bg:            #0b0e12;  /* Genesis altus-black */
  --surface:       #171a1f;  /* Genesis panel-bg */
  --surface-2:     #1c2026;  /* Genesis panel-elev */
  --border:        #2a2f36;  /* Genesis line */

  --text:          #e8eaf0;  /* Genesis text */
  --text-subtle:   #a6adbb;  /* Genesis text-dim */

  --gold:          #d6b36a;  /* Genesis Altus Gold */
  --gold-700:      #c7a55a;  /* hover/pressed Altus Gold */

  --panel-bg:      #171a1f;
  --panel-elev:    #1c2026;
  --line:          #2a2f36;
  --text-dim:      #a6adbb;
  --link:          #8AB4FF;  /* hyperlinks only */

  --success:       #2cc38a;  /* Genesis good */
  --warning:       #f3c969;  /* Genesis warn */
  --danger:        #ef5953;  /* Genesis bad */

  --good:          #2cc38a;
  --warn:          #f3c969;
  --bad:           #ef5953;
  --neutral:       #8b93a3;
  --info:          #5CC8FF;

  /* Component Anatomy */
  --radius-sm:     10px;
  --radius-md:     12px;
  --radius-lg:     14px;
  --radius-xl:     18px;
  --radius-pill:   999px;

  --gap-1:          8px;
  --gap-2:         12px;
  --gap-3:         16px;
  --gap-4:         24px;

  /* Elevation (subtle, crisp) */
  --shadow-1: 0 1px 0 rgba(0,0,0,.35), 0 6px 18px rgba(0,0,0,.22);
  --shadow-2: 0 0 0 1px rgba(255,255,255,.02) inset, 0 12px 28px rgba(0,0,0,.30);

  /* Typography */
  --font: ui-sans-serif, system-ui, -apple-system, "Segoe UI", Inter, Roboto, "Helvetica Neue", Arial, "Noto Sans", "Apple Color Emoji", "Segoe UI Emoji";
  --fs-12: 12px;  --fs-14: 14px;  --fs-16: 16px;  --fs-18: 18px;  --fs-22: 22px;  --fs-28: 28px;
  --lh-tight: 1.15;  --lh-normal: 1.35;

  /* Motion */
  --ease: cubic-bezier(.2,.8,.2,1);
  --t-fast: 120ms; --t-med: 180ms;

  /* Focus (high contrast) */
  --focus: 0 0 0 2px #000, 0 0 0 4px var(--gold);
}

/* --- ECC Object Surface Tokens (canonical) --- */
:root, .theme-ecc, [data-theme="ecc"] {
  --ecc-object-bg: #2A2F38;           /* light grey (on dark) for inner boxes */
  --ecc-object-border: rgba(255,255,255,0.08);
  --ecc-object-radius: 16px;
  --ecc-object-pad: 16px;
}

html, body {
  background: var(--surface-app);
  color: var(--text-normal);
  font-family: var(--font);
}

/* Skeleton shimmer (shared) */
@keyframes ecc-shimmer {
  0% { background-position: 0% 0; }
  100% { background-position: 200% 0; }
}
/* ===== END THEME_TOKENS ===== */

