import { useEffect, useState } from "react";

export type BadgeCounts = {
  workOrdersOpen?: number;
  collectionsOpen?: number;
  inventoryLow?: number;
  // add more keys as needed
};

const initial: BadgeCounts = {
  workOrdersOpen: 12,
  collectionsOpen: 3,
  inventoryLow: 5,
};

export function useBadgeCounts(): BadgeCounts {
  const [counts, setCounts] = useState<BadgeCounts>(initial);

  // Placeholder polling â€” replace with real API calls.
  useEffect(() => {
    const id = setInterval(() => {
      setCounts((c) => ({ ...c })); // no-op; hook shows where to refresh
    }, 30000);
    return () => clearInterval(id);
  }, []);

  return counts;
}

src/components/data/Sparkline.tsx.content:
// src/components/data/Sparkline.tsx
import React from "react";

type Props = { data: number[]; width?: number; height?: number; ariaLabel?: string };

export default function Sparkline({ data, width = 120, height = 36, ariaLabel = "trend" }: Props) {
  if (!data || data.length === 0) {
    return <div style={{ width, height }} aria-label={ariaLabel} />;
  }
  const min = Math.min(...data);
  const max = Math.max(...data);
  const span = max - min || 1;
  const stepX = width / (data.length - 1 || 1);
  const pts = data
    .map((v, i) => {
      const x = i * stepX;
      const y = height - ((v - min) / span) * height;
      return `${x.toFixed(2)},${y.toFixed(2)}`;
    })
    .join(" ");

  return (
    <svg width={width} height={height} viewBox={`0 0 ${width} ${height}`} aria-label={ariaLabel}>
      <polyline
        points={pts}
        fill="none"
        stroke="currentColor"
        strokeOpacity="0.8"
        strokeWidth="2"
      />
    </svg>
  );
}

