src/styles/table.css.content:
/* /src/styles/table.css
   Genesis dark: higher contrast, banded rows, sticky header+filters, right-aligned numbers,
   polished paddings, hover cues, skeletons, and drawer.
*/

/* Genesis Level Altus Branding */
@import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700;800&display=swap');

:root{
  /* Altus Genesis Branding */
  --altus-black: #0b0e12;
  --altus-panel: #1a1d21;            /* existing panels */
  --altus-grey-600: #2a2d31;         /* APPROVED LIGHT GREY for chips/cards */
  --altus-grey-700: #2f353d;         /* alt zebra / chip hover */
  --altus-gold: #d6b36a;             /* keep for accents and progress */
  --altus-text: #d7dae0;
  --altus-muted: #a0a0a0;

  /* Legacy aliases */
  --altus-dark-gray: var(--altus-panel);
  --altus-light-gray: var(--altus-muted);

  /* Legacy mappings for compatibility */
  --ink-1: var(--altus-text);
  --ink-2: #c9cbd0;
  --ink-3: var(--altus-muted);
  --surface-0: var(--altus-black);
  --surface-1: var(--altus-panel);
  --surface-2: #1f2226;
  --surface-3: #242831;
  --line-1: var(--altus-grey-600);
  --accent: var(--altus-gold);

  /* Status Colors */
  --status-active: #34d399;
  --status-active-bg: rgba(52, 211, 153, 0.1);
  --status-active-border: rgba(52, 211, 153, 0.3);
  --status-litigation: #ef4444;
  --status-litigation-bg: rgba(239, 68, 68, 0.1);
  --status-litigation-border: rgba(239, 68, 68, 0.3);
  --status-warning: #f59e0b;
  --status-warning-bg: rgba(245, 158, 11, 0.1);
  --status-warning-border: rgba(245, 158, 11, 0.3);
}

/* ---------- Genesis Typography ---------- */
* {
  font-family: 'Inter', -apple-system, BlinkMacSystemFont, sans-serif;
}

/* ---------- Toolbar ---------- */
.ecc-table-toolbar{
  display:flex; align-items:center; gap:12px;
  margin: 10px 0 14px;
}
.ecc-input, .ecc-select{
  background: var(--surface-2); color: var(--altus-text);
  border: 1px solid var(--line-1); border-radius: 10px;
  padding: 8px 12px; min-width: 220px; outline:none;
  font-weight: 500;
  box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1);
  transition: all 0.2s ease;
}
.ecc-input:focus, .ecc-select:focus{
  border-color: var(--altus-gold);
  box-shadow: 0 0 0 2px rgba(214, 179, 106, 0.2);
}
.ecc-input::placeholder{ color: var(--altus-light-gray); }
.ecc-select{ min-width: 120px; }
.ecc-btn{
  background: var(--surface-2); color: var(--altus-text);
  border: 1px solid var(--line-1); border-radius: 10px; 
  padding: 8px 16px; font-weight: 600;
  box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1);
  transition: all 0.2s ease;
  cursor: pointer;
}
.ecc-btn:hover:not(:disabled){
  background: var(--surface-3);
  border-color: var(--altus-gold);
  transform: translateY(-1px);
  box-shadow: 0 2px 6px 0 rgba(0, 0, 0, 0.2);
}
.ecc-btn:disabled{ opacity: .5; cursor: not-allowed; }
.ecc-toolbar-spacer{ flex:1; }

/* ---------- Table shell ---------- */
.ecc-table-wrap{ width:100%; }
.ecc-table{
  width: 100%;
  border-collapse: separate;
  border-spacing: 0;
  background: var(--surface-1);
  border: 1px solid var(--line-1);
  border-radius: 14px;
  overflow: hidden;
  box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
}

/* ---------- Header ---------- */
.ecc-table thead th{
  top: 0;
  position: sticky;
  z-index: 2;
  background: var(--surface-1);
  color: var(--altus-text);
  font-weight: 700;
  font-size: 0.75rem;
  letter-spacing: 0.05em;
  text-transform: uppercase;
  text-align: left;
  padding: 16px 14px;
  border-bottom: 1px solid var(--line-1);
  border-left: 1px solid var(--altus-grey-600);
  border-right: 1px solid var(--altus-grey-600);
  white-space: nowrap;
}
.ecc-table thead th:first-child{
  border-left: none;
}
.ecc-table thead th:last-child{
  border-right: none;
}
.ecc-table thead th.is-clickable{ 
  cursor: pointer; 
  transition: all 0.2s ease;
}
.ecc-table thead th.is-clickable:hover{
  color: var(--altus-gold);
  background: var(--surface-2);
}
.ecc-table thead th.asc::after{ content: " â–²"; color: var(--altus-gold); font-weight: 900; }
.ecc-table thead th.desc::after{ content: " â–¼"; color: var(--altus-gold); font-weight: 900; }

/* Sticky filter row under header */
.ecc-filter-row th{
  position: sticky;
  top: 44px; /* header height */
  z-index: 1;
  background: var(--surface-1);
  border-bottom: 1px solid var(--line-1);
  border-left: 1px solid var(--altus-grey-600);
  border-right: 1px solid var(--altus-grey-600);
  padding: 8px 14px;
}
.ecc-filter-row th:first-child{
  border-left: none;
}
.ecc-filter-row th:last-child{
  border-right: none;
}
.ecc-range{ display:flex; gap:8px; }
.ecc-input--min, .ecc-input--max{ width: 90px; min-width: 90px; }

/* ---------- Body ---------- */
.ecc-table tbody tr:nth-child(odd){ background: var(--surface-2); }
.ecc-table tbody tr:nth-child(even){ background: #171b20; /* slightly lighter for contrast */ }
.ecc-table tbody tr{
  cursor: pointer;
  transition: all 0.2s ease;
}
.ecc-table tbody tr:hover{ 
  background: #1e232a; /* subtle hover */
  transform: translateY(-1px);
  box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.1);
}
.ecc-row:nth-child(even) { 
  background: #171b20; 
}
.ecc-row:hover { 
  background: #1e232a; 
}
.ecc-table td{
  padding: 14px;
  border-bottom: 1px solid var(--line-1);
  border-left: 1px solid var(--altus-grey-600);
  border-right: 1px solid var(--altus-grey-600);
  color: var(--altus-text);
  font-weight: 500;
  vertical-align: middle;
}
.ecc-table td:first-child{
  border-left: none;
}
.ecc-table td:last-child{
  border-right: none;
}
.ecc-table td.is-right{ text-align:right; }
.ecc-table td.is-empty{ 
  text-align:center; 
  color: var(--altus-light-gray); 
  padding: 32px;
  font-weight: 600;
}

/* ---------- Footer ---------- */
.ecc-table-footer{
  display:flex; align-items:center; justify-content:space-between;
  padding: 12px 8px; color: var(--ink-3);
}
.ecc-page{ padding: 0 10px; }
.ecc-pager{ display:flex; align-items:center; gap:10px; }

/* ---------- Genesis Status Tags ---------- */
.ecc-badge {
  display: inline-flex;
  align-items: center;
  padding: 4px 12px;
  border-radius: 9999px;
  font-weight: 600;
  font-size: 0.75rem;
  letter-spacing: 0.025em;
  text-transform: uppercase;
  background: var(--altus-grey-600);
  color: var(--altus-text);
  border: 1px solid rgba(255,255,255,0.06);
}
.ecc-badge--good { 
  background: #1f6f4a; /* green for positive */
}
.ecc-badge--warn { 
  background: var(--altus-gold); 
  color: #111; /* keep gold for ATTENTION states */
}
.ecc-badge--bad { 
  background: #80343a; /* red for negative */
}
.ecc-badge:hover { 
  background: var(--altus-grey-700); 
}

/* ---------- Progress Bars ---------- */
.ecc-progress-bar {
  display: inline-flex;
  align-items: center;
  gap: 8px;
  min-width: 120px;
}
.ecc-progress {
  background: #21262c; /* darker neutral track */
  height: 6px;
  border-radius: 3px;
  overflow: hidden;
  flex: 1;
}
.ecc-progress__fill {
  background: var(--altus-gold); /* keep gold fill */
  height: 100%;
  border-radius: 3px;
  transition: width 0.3s ease;
}
.ecc-progress-bg {
  background: #21262c;
  height: 6px;
  border-radius: 3px;
  overflow: hidden;
  flex: 1;
}
.ecc-progress-fg {
  background: var(--altus-gold);
  height: 100%;
  border-radius: 3px;
  transition: width 0.3s ease;
}
.ecc-progress-text {
  font-size: 0.75rem;
  font-weight: 600;
  color: var(--altus-text);
  min-width: 40px;
}

/* ---------- Skeletons ---------- */
.ecc-skel-row .ecc-skel{
  height: 12px; border-radius: 6px; width: 100%;
  background: linear-gradient(90deg, #20242d 25%, #2a2f3b 37%, #20242d 63%);
  background-size: 400% 100%;
  animation: eccShimmer 1.2s ease-in-out infinite;
}
@keyframes eccShimmer {
  0% { background-position: 100% 0; }
  100% { background-position: 0 0; }
}

/* Drawer: INLINE, never full-screen */
.ecc-drawer{
  position: static;           /* was: fixed; inset:0 */
  display: block;
  z-index: auto;
}
.ecc-drawer__backdrop{
  display: none !important;   /* never render a page mask */
}
.ecc-drawer__panel{
  position: relative;
  width: 100%;
  max-width: 100%;
  height: auto;               /* was: 100% */
  background: var(--surface-1);
  border: 1px solid var(--line-1);
  border-radius: 12px;
  overflow: hidden;
}
.ecc-drawer__head{ display:flex; align-items:center; justify-content:space-between;
  padding: 14px; border-bottom:1px solid var(--line-1); }
.ecc-drawer__title{ font-weight:800; color:var(--ink-1); }
.ecc-drawer__body{ padding:14px; overflow:auto; max-height: 60vh; }
.ecc-grid{ display:grid; grid-template-columns: repeat(2, minmax(0,1fr)); gap: 12px; }
.ecc-field__label{ color: var(--altus-light-gray); font-size: 12px; margin-bottom: 4px; text-transform: uppercase; letter-spacing: 0.05em; }
.ecc-field__value{ color: var(--altus-text); font-weight: 600; }

/* ---------- Genesis KPI Cards ---------- */
.ecc-kpis {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 16px;
  padding: 20px 0;
  margin-bottom: 24px;
}

.ecc-kpi,
.ecc-card {
  background: var(--altus-grey-600);
  border: 1px solid rgba(255,255,255,0.06);
  border-radius: 12px;
  padding: 20px;
  text-align: center;
  box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.1);
  transition: all 0.2s ease;
}

.ecc-kpi:hover {
  transform: translateY(-2px);
  box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.15);
}

.ecc-kpi-n {
  font-size: 2rem;
  font-weight: 800;
  color: var(--altus-gold);
  line-height: 1;
  margin-bottom: 8px;
}

.ecc-kpi-l {
  font-size: 0.75rem;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.05em;
  color: var(--altus-light-gray);
}

/* ---------- Page Layout ---------- */
.ecc-page {
  padding: 24px;
  background: var(--altus-black);
  min-height: 100vh;
  font-family: 'Inter', sans-serif;
}

.ecc-page-title {
  font-size: 2rem;
  font-weight: 800;
  color: var(--altus-text);
  margin-bottom: 32px;
  letter-spacing: -0.025em;
}

/* ---------- Row Actions Menu ---------- */
.ecc-row-actions {
  position: relative;
  display: inline-block;
}

.ecc-actions-trigger {
  width: 24px;
  height: 24px;
  border-radius: 50%;
  background: transparent;
  color: var(--altus-light-gray);
  border: none;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 16px;
  transition: all 0.2s ease;
}

.ecc-actions-trigger:hover {
  background: var(--surface-3);
  color: var(--altus-gold);
}

.ecc-actions-dropdown {
  top: 100%;
  right: 0;
  background: var(--altus-dark-gray);
  border: 1px solid var(--line-1);
  border-radius: 8px;
  box-shadow: 0 8px 16px 0 rgba(0, 0, 0, 0.2);
  min-width: 180px;
  overflow: hidden;
  margin-top: 4px;
}

.ecc-actions-dropdown button {
  display: block;
  width: 100%;
  padding: 12px 16px;
  background: transparent;
  color: var(--altus-text);
  border: none;
  text-align: left;
  font-size: 0.875rem;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s ease;
}

.ecc-actions-dropdown button:hover {
  background: var(--surface-3);
  color: var(--altus-gold);
}

