import React from "react";
import "@/styles/table.css";

type Column<T> = { key: keyof T & string; label: string; grow?: boolean; align?: "left"|"right"|"center" };

export function SimpleTable<T extends Record<string, any>>({
  columns,
  rows,
  empty = "No data",
}: {
  columns: Column<T>[];
  rows: T[];
  empty?: string;
}) {
  return (
    <div className="table-wrap">
      <table className="ecc-table">
        <thead>
          <tr>
            {columns.map((c) => (
              <th
                key={c.key}
                style={{ textAlign: c.align ?? "left" }}
                className={c.grow ? "grow" : undefined}
              >
                {c.label}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {rows.length === 0 && (
            <tr>
              <td colSpan={columns.length} className="empty">{empty}</td>
            </tr>
          )}
          {rows.map((r, i) => (
            <tr key={i}>
              {columns.map((c) => (
                <td key={c.key} style={{ textAlign: c.align ?? "left" }}>
                  {display(r[c.key])}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

function display(v: any) {
  if (v == null) return "â€”";
  if (Array.isArray(v)) return v.join(", ");
  if (typeof v === "number") return Number.isFinite(v) ? v.toLocaleString() : "â€”";
  return String(v);
}

