/* Main content area, shifted to the right of sidebar */
.ecc-main {
  position: relative;
  min-height: 100vh;
  transition: padding-left 160ms ease;
  z-index: 0; /* sidebar flyouts sit above this */
}

/* Inner wrapper for content spacing */
.ecc-main-inner {
  max-width: 1920px;
  margin: 0 auto;
  padding: 16px clamp(12px, 2vw, 24px);
}

