export function toCSV<T extends Record<string, any>>(rows: T[], columns: { key: string; header: string }[]) {
  const header = columns.map(c => safe(c.header)).join(",");
  const body = rows.map(r => columns.map(c => safe(r?.[c.key])).join(",")).join("\n");
  return header + "\n" + body;
}
function safe(v: any) {
  if (v == null) return "";
  const s = String(v).replace(/"/g, '""');
  return /[",\n]/.test(s) ? `"${s}"` : s;
}
export function downloadCSV(filename: string, csv: string) {
  const blob = new Blob([csv], { type: "text/csv;charset=utf-8" });
  const url = URL.createObjectURL(blob);
  const a = document.createElement("a");
  a.href = url; a.download = filename;
  document.body.appendChild(a); a.click();
  a.remove(); URL.revokeObjectURL(url);
}

src/utils/dict.ts.content:
type Keyer<T, K extends PropertyKey> = keyof T | ((row: T) => K);

function getKey<T, K extends PropertyKey>(row: T, keyer: Keyer<T, K>): K {
  if (typeof keyer === "function") return keyer(row);
  return (row as any)[keyer] as K;
}

/** Make a map {id: row} */
export function indexBy<T, K extends PropertyKey>(
  rows: T[] = [],
  keyer: Keyer<T, K>
): Map<K, T> {
  return rows.reduce((acc, row) => {
    const k = getKey(row, keyer);
    acc.set(k, row);
    return acc;
  }, new Map<K, T>());
}

/** Group into {key: T[]} */
export function groupBy<T, K extends PropertyKey>(
  rows: T[] = [],
  keyer: Keyer<T, K>
): Map<K, T[]> {
  return rows.reduce((acc, row) => {
    const k = getKey(row, keyer);
    const existing = acc.get(k) || [];
    existing.push(row);
    acc.set(k, existing);
    return acc;
  }, new Map<K, T[]>());
}

