import { boolean, varchar, integer, serial, text, timestamp, decimal } from "drizzle-orm/pg-core";
import { pgTable, pgSchema } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// Owner transfer status enum for type safety and state machine logic
export enum OwnerTransferStatus {
  PENDING_ACCOUNTING = "PENDING_ACCOUNTING",
  APPROVED_ACCOUNTING = "APPROVED_ACCOUNTING", 
  READY_EXECUTION = "READY_EXECUTION",
  COMPLETE = "COMPLETE",
  FAILED = "FAILED"
}

// Define valid state transitions for owner transfer workflow
export const OWNER_TRANSFER_STATE_TRANSITIONS: Record<OwnerTransferStatus, OwnerTransferStatus[]> = {
  [OwnerTransferStatus.PENDING_ACCOUNTING]: [OwnerTransferStatus.APPROVED_ACCOUNTING, OwnerTransferStatus.FAILED],
  [OwnerTransferStatus.APPROVED_ACCOUNTING]: [OwnerTransferStatus.READY_EXECUTION, OwnerTransferStatus.FAILED],
  [OwnerTransferStatus.READY_EXECUTION]: [OwnerTransferStatus.COMPLETE, OwnerTransferStatus.FAILED],
  [OwnerTransferStatus.COMPLETE]: [], // Terminal state
  [OwnerTransferStatus.FAILED]: [OwnerTransferStatus.PENDING_ACCOUNTING] // Can restart from beginning
};

// Helper function to check if status transition is valid
export function isValidStatusTransition(currentStatus: OwnerTransferStatus, newStatus: OwnerTransferStatus): boolean {
  return OWNER_TRANSFER_STATE_TRANSITIONS[currentStatus].includes(newStatus);
}

// Properties table with lat/lng columns for geocoding
export const properties = pgTable("properties", {
  id: serial("id").primaryKey(),
  name: varchar("name", { length: 255 }),
  type: varchar("type", { length: 100 }),
  class: varchar("class", { length: 100 }),
  address_state: varchar("address_state", { length: 2 }),
  address_city: varchar("address_city", { length: 100 }),
  address_zip: varchar("address_zip", { length: 10 }),
  property_address: varchar("property_address", { length: 500 }),
  address: varchar("address", { length: 500 }),
  street_address: varchar("street_address", { length: 500 }),
  full_address: varchar("full_address", { length: 500 }),
  active: boolean("active").default(true),
  lat: decimal("lat", { precision: 10, scale: 8 }),
  lng: decimal("lng", { precision: 11, scale: 8 }),
  doorloop_id: text("doorloop_id"),
  owner_id: integer("owner_id").references(() => owners.id),
  created_at: timestamp("created_at").defaultNow(),
  updated_at: timestamp("updated_at").defaultNow(),
});

// Units table
export const units = pgTable("units", {
  id: serial("id").primaryKey(),
  property_id: integer("property_id").references(() => properties.id),
  name: varchar("name", { length: 100 }),
  unit_number: varchar("unit_number", { length: 50 }),
  unit_name: varchar("unit_name", { length: 100 }),
  beds: integer("beds"),
  baths: decimal("baths", { precision: 3, scale: 1 }),
  sqft: integer("sqft"),
  status: varchar("status", { length: 50 }),
  marketRent: decimal("marketRent", { precision: 10, scale: 2 }),
  market_rent: decimal("market_rent", { precision: 10, scale: 2 }),
  rent: decimal("rent", { precision: 10, scale: 2 }),
  condition: varchar("condition", { length: 50 }),
  unit_condition: varchar("unit_condition", { length: 50 }),
  doorloop_id: text("doorloop_id"),
  created_at: timestamp("created_at").defaultNow(),
  updated_at: timestamp("updated_at").defaultNow(),
});

// Leases table
export const leases = pgTable("leases", {
  id: serial("id").primaryKey(),
  property_id: integer("property_id").references(() => properties.id),
  unit_id: integer("unit_id").references(() => units.id),
  tenant_id: integer("tenant_id").references(() => tenants.id),
  unitDbId: integer("unitDbId").references(() => units.id),
  unitId: integer("unitId").references(() => units.id),
  status: varchar("status", { length: 50 }),
  start: timestamp("start"),
  end: timestamp("end"),
  start_date: timestamp("start_date"),
  end_date: timestamp("end_date"),
  rent: decimal("rent", { precision: 10, scale: 2 }),
  tenants: text("tenants").array(),
  tenant_name: varchar("tenant_name", { length: 255 }),
  totalBalanceDue: decimal("totalBalanceDue", { precision: 10, scale: 2 }),
  balance_due: decimal("balance_due", { precision: 10, scale: 2 }),
  outstanding_balance: decimal("outstanding_balance", { precision: 10, scale: 2 }),
  doorloop_id: text("doorloop_id"),
  created_at: timestamp("created_at").defaultNow(),
  updated_at: timestamp("updated_at").defaultNow(),
});

// Tenants table
export const tenants = pgTable("tenants", {
  id: serial("id").primaryKey(),
  name: varchar("name", { length: 255 }),
  email: varchar("email", { length: 255 }),
  phone: varchar("phone", { length: 20 }),
  type: varchar("type", { length: 50 }),
  balance: decimal("balance", { precision: 10, scale: 2 }),
  current_balance: decimal("current_balance", { precision: 10, scale: 2 }),
  outstanding_balance: decimal("outstanding_balance", { precision: 10, scale: 2 }),
  doorloop_tenant_id: text("doorloop_tenant_id"),
  created_at: timestamp("created_at").defaultNow(),
  updated_at: timestamp("updated_at").defaultNow(),
});

// Owners table
export const owners = pgTable("owners", {
  id: serial("id").primaryKey(),
  company: varchar("company", { length: 255 }),
  name: varchar("name", { length: 255 }),
  email: varchar("email", { length: 255 }),
  phone: varchar("phone", { length: 20 }),
  active: boolean("active").default(true),
  doorloop_owner_id: text("doorloop_owner_id"),
  created_at: timestamp("created_at").defaultNow(),
  updated_at: timestamp("updated_at").defaultNow(),
});

// Workorders table for maintenance
export const workorders = pgTable("workorders", {
  id: serial("id").primaryKey(),
  property_id: integer("property_id").references(() => properties.id),
  unit_id: integer("unit_id").references(() => units.id),
  unitId: integer("unitId").references(() => units.id),
  title: varchar("title", { length: 255 }),
  description: text("description"),
  issue: text("issue"),
  summary: text("summary"),
  status: varchar("status", { length: 50 }),
  priority: varchar("priority", { length: 50 }),
  priority_level: varchar("priority_level", { length: 50 }),
  created_at: timestamp("created_at").defaultNow(),
  updated_at: timestamp("updated_at").defaultNow(),
});

// Geocode cache table for efficient address lookups
export const geocodeCache = pgTable("geocode_cache", {
  id: serial("id").primaryKey(),
  address: varchar("address", { length: 500 }).unique(),
  lat: decimal("lat", { precision: 10, scale: 8 }),
  lng: decimal("lng", { precision: 11, scale: 8 }),
  provider: varchar("provider", { length: 50 }),
  confidence: decimal("confidence", { precision: 5, scale: 4 }),
  created_at: timestamp("created_at").defaultNow(),
  updated_at: timestamp("updated_at").defaultNow(),
});

// Owner transfers table for tracking property ownership transfers
export const ownerTransfers = pgTable("owner_transfers", {
  id: serial("id").primaryKey(),
  property_ids: text("property_ids").array(), // JSON array of property IDs
  new_owner_id: integer("new_owner_id").references(() => owners.id),
  old_owner_id: integer("old_owner_id").references(() => owners.id),
  effective_date: varchar("effective_date", { length: 10 }), // YYYY-MM-DD format
  status: varchar("status", { length: 50 }).default("PENDING_ACCOUNTING"), // PENDING_ACCOUNTING, APPROVED_ACCOUNTING, READY_EXECUTION, COMPLETE
  notes: text("notes"),
  initiated_by: varchar("initiated_by", { length: 255 }),
  executed_at: timestamp("executed_at"),
  created_at: timestamp("created_at").defaultNow(),
  updated_at: timestamp("updated_at").defaultNow(),
});

// Owner transfer snapshots for storing immutable entity states
export const ownerTransferSnapshots = pgTable("owner_transfer_snapshots", {
  id: serial("id").primaryKey(),
  transfer_id: integer("transfer_id").references(() => ownerTransfers.id),
  entity_type: varchar("entity_type", { length: 50 }), // 'property', 'unit', 'lease', 'tenant', etc.
  entity_id: integer("entity_id"), // Integer ID to handle primary keys
  raw_jsonb: text("raw_jsonb"), // JSON snapshot of the entity (using text for compatibility)
  created_at: timestamp("created_at").defaultNow(),
});

// Audit events table for tracking all system changes
export const auditEvents = pgTable("audit_events", {
  id: serial("id").primaryKey(),
  event_type: varchar("event_type", { length: 100 }).notNull(), // e.g., 'OWNER_TRANSFER_INIT', 'OWNER_TRANSFER_COMPLETE'
  ref_table: varchar("ref_table", { length: 50 }), // 'owner_transfers', 'properties', etc.
  ref_id: integer("ref_id"), // ID of the affected entity
  actor_id: varchar("actor_id", { length: 255 }), // User who performed the action
  payload: text("payload"), // JSON payload with additional context (using text for compatibility)
  tag: varchar("tag", { length: 100 }), // Optional tag for grouping events
  created_at: timestamp("created_at").defaultNow(),
});

// Insert schemas for form validation
export const insertPropertySchema = createInsertSchema(properties);
export const insertUnitSchema = createInsertSchema(units);
export const insertLeaseSchema = createInsertSchema(leases);
export const insertTenantSchema = createInsertSchema(tenants);
export const insertOwnerSchema = createInsertSchema(owners);
export const insertWorkorderSchema = createInsertSchema(workorders);
export const insertOwnerTransferSchema = createInsertSchema(ownerTransfers);
export const insertOwnerTransferSnapshotSchema = createInsertSchema(ownerTransferSnapshots);
export const insertAuditEventSchema = createInsertSchema(auditEvents);

// Types for TypeScript
export type Property = typeof properties.$inferSelect;
export type InsertProperty = z.infer<typeof insertPropertySchema>;
export type Unit = typeof units.$inferSelect;
export type InsertUnit = z.infer<typeof insertUnitSchema>;
export type Lease = typeof leases.$inferSelect;
export type InsertLease = z.infer<typeof insertLeaseSchema>;
export type Tenant = typeof tenants.$inferSelect;
export type InsertTenant = z.infer<typeof insertTenantSchema>;
export type Owner = typeof owners.$inferSelect;
export type InsertOwner = z.infer<typeof insertOwnerSchema>;
export type Workorder = typeof workorders.$inferSelect;
export type InsertWorkorder = z.infer<typeof insertWorkorderSchema>;
export type OwnerTransfer = typeof ownerTransfers.$inferSelect;
export type InsertOwnerTransfer = z.infer<typeof insertOwnerTransferSchema>;
export type OwnerTransferSnapshot = typeof ownerTransferSnapshots.$inferSelect;
export type InsertOwnerTransferSnapshot = z.infer<typeof insertOwnerTransferSnapshotSchema>;
export type AuditEvent = typeof auditEvents.$inferSelect;
export type InsertAuditEvent = z.infer<typeof insertAuditEventSchema>;

// Enhanced owner transfer type with status enum validation
export interface EnhancedOwnerTransfer extends OwnerTransfer {
  status: OwnerTransferStatus;
}

// Validation schema for owner transfer status updates
export const ownerTransferStatusSchema = z.object({
  transfer_id: z.number().positive(),
  new_status: z.nativeEnum(OwnerTransferStatus),
  notes: z.string().optional(),
  actor_id: z.string().optional()
});

server/services/ownerTransferService.ts.content:
import { z } from "zod";
import ExcelJS from "exceljs";
import { supabaseAdmin } from "../lib/supabaseAdmin";
import { auditEvent } from "../lib/audit";
import { uploadBufferToDropbox } from "../lib/dropbox";

const InitiateSchema = z.object({
  propertyIds: z.array(z.number().int()).min(1),
  newOwnerId: z.number().int(),
  effectiveDate: z.string().regex(/^\d{4}-\d{2}-\d{2}$/),
  notes: z.string().optional(),
  initiatedBy: z.string().uuid().optional(),
});

export type InitiateInput = z.infer<typeof InitiateSchema>;

// Helpers â€” skip missing tables
async function safeSelect<T = any>(table: string, col: string, values: readonly any[]) {
  try {
    const q = supabaseAdmin.from(table).select("*").in(col, values);
    // @ts-ignore
    const { data, error } = await q;
    if (error) {
      if ((error as any)?.code === "42P01") return []; // table missing
      throw error;
    }
    return data ?? [];
  } catch (e: any) {
    if (String(e?.message || "").includes("relation") && String(e?.message || "").includes("does not exist")) return [];
    throw e;
  }
}

export async function initiateTransfer(input: InitiateInput) {
  const parsed = InitiateSchema.parse(input);

  // Create transfer record (sets status = PENDING_ACCOUNTING)
  const { data, error } = await supabaseAdmin
    .from("owner_transfers")
    .insert({
      property_ids: parsed.propertyIds,
      old_owner_id: null,
      new_owner_id: parsed.newOwnerId,
      effective_date: parsed.effectiveDate,
      status: "PENDING_ACCOUNTING",
      notes: parsed.notes ?? null,
      initiated_by: parsed.initiatedBy ?? null,
    })
    .select("id")
    .single();
  if (error) throw error;
  const transferId = data.id as number;

  await auditEvent({ 
    event_type: "OWNER_TRANSFER_INIT", 
    ref_table: "owner_transfers", 
    ref_id: transferId, 
    payload: { input: parsed } 
  });

  // infer old_owner_id from first property
  const first = await supabaseAdmin.from("properties").select("owner_id").eq("id", parsed.propertyIds[0]).single();
  if (!first.error && first.data) {
    await supabaseAdmin.from("owner_transfers").update({ old_owner_id: first.data.owner_id }).eq("id", transferId);
  }

  // Snapshots (skip slices that 404)
  const properties = await safeSelect("properties", "id", parsed.propertyIds);
  const units = await safeSelect("units", "property_id", parsed.propertyIds);
  const leases = await safeSelect("leases", "property_id", parsed.propertyIds);

  const tenantIds = Array.from(new Set(leases.map((l: any) => l.primary_tenant_id).filter(Boolean)));
  const tenants = tenantIds.length ? await safeSelect("tenants", "id", tenantIds) : [];

  const leaseIds = Array.from(new Set(leases.map((l: any) => l.id)));
  const lease_payments = leaseIds.length ? await safeSelect("lease_payments", "lease_id", leaseIds) : [];
  const lease_charges = leaseIds.length ? await safeSelect("lease_charges", "lease_id", leaseIds) : [];
  const lease_credits = leaseIds.length ? await safeSelect("lease_credits", "lease_id", leaseIds) : [];

  const work_orders = await safeSelect("work_orders", "property_id", parsed.propertyIds);
  const communications = await safeSelect("communications", "property_id", parsed.propertyIds);
  const files = await safeSelect("files", "property_id", parsed.propertyIds);
  const notes = await safeSelect("notes", "property_id", parsed.propertyIds);

  const bundle = { 
    properties, units, leases, tenants, lease_payments, lease_charges, lease_credits, 
    work_orders, communications, files, notes 
  };

  const rows: any[] = [];
  const push = (et: string, arr: any[], idKey = "id") => 
    arr.forEach(r => rows.push({ 
      transfer_id: transferId, 
      entity_type: et.replace(/s$/, ''), // singularize
      entity_id: r?.[idKey] ?? 0, 
      raw_jsonb: JSON.stringify(r) 
    }));

  Object.entries(bundle).forEach(([k, v]) => push(k, v as any[]));

  // Chunk insert to avoid timeouts
  for (let i = 0; i < rows.length; i += 500) {
    const slice = rows.slice(i, i + 500);
    const { error: e } = await supabaseAdmin.from("owner_transfer_snapshots").insert(slice);
    if (e) throw e;
  }

  await auditEvent({
    event_type: "OWNER_TRANSFER_SNAPSHOT_DONE",
    ref_table: "owner_transfers",
    ref_id: transferId,
    payload: Object.fromEntries(Object.entries(bundle).map(([k, v]) => [k, (v as any[])?.length || 0])),
  });

  // Build report now to unblock accounting
  const rep = await generateAccountingReport(transferId);
  return { transferId, reportUrl: rep.reportUrl ?? null };
}

export async function generateAccountingReport(transferId: number) {
  const t = await supabaseAdmin.from("owner_transfers").select("*").eq("id", transferId).single();
  if (t.error) throw t.error;
  const transfer = t.data;

  const wb = new ExcelJS.Workbook();
  wb.creator = "ECC";
  wb.created = new Date();

  const summary = wb.addWorksheet("Summary");
  summary.columns = [
    { header: "Transfer ID", key: "id" },
    { header: "Old Owner ID", key: "old_owner_id" },
    { header: "New Owner ID", key: "new_owner_id" },
    { header: "Effective Date", key: "effective_date" },
    { header: "Status", key: "status" },
  ];
  summary.addRow(transfer);

  // TODO: add Income/Expenses/Pending sheets by querying your accounting tables

  const buffer = Buffer.from(await wb.xlsx.writeBuffer());
  const filename = `owner_transfer_${transferId}.xlsx`;

  let reportUrl: string | undefined;
  if ((process.env.OWNER_TRANSFER_REPORT_DEST || "dropbox") === "dropbox") {
    try {
      const up = await uploadBufferToDropbox({ 
        path: `/reports/owner_transfers/${filename}`, 
        buffer 
      });
      reportUrl = up.url;
    } catch (err) {
      console.error("Dropbox upload failed, continuing without cloud storage:", err);
      // Continue without cloud storage if Dropbox fails
    }
  }

  await auditEvent({ 
    event_type: "OWNER_TRANSFER_REPORT_BUILT", 
    ref_table: "owner_transfers", 
    ref_id: transferId, 
    payload: { reportUrl: reportUrl || null, filename } 
  });

  return { buffer, filename, reportUrl };
}

export async function markApprovedByAccounting(transferId: number, actorId?: string) {
  const { error } = await supabaseAdmin
    .from("owner_transfers")
    .update({ status: "APPROVED_ACCOUNTING" })
    .eq("id", transferId);
  if (error) throw error;

  await auditEvent({ 
    event_type: "OWNER_TRANSFER_APPROVE_ACCOUNTING", 
    ref_table: "owner_transfers", 
    ref_id: transferId, 
    actor_id: actorId ?? null 
  });
}

export async function authorizeExecution(transferId: number, actorId?: string) {
  const { error } = await supabaseAdmin
    .from("owner_transfers")
    .update({ status: "READY_EXECUTION" })
    .eq("id", transferId);
  if (error) throw error;

  await auditEvent({ 
    event_type: "OWNER_TRANSFER_AUTHORIZED", 
    ref_table: "owner_transfers", 
    ref_id: transferId, 
    actor_id: actorId ?? null 
  });
}

export async function executeTransfer(transferId: number, opts?: { dryRun?: boolean }) {
  const dryRun = opts?.dryRun !== false; // default true

  const t = await supabaseAdmin.from("owner_transfers").select("*").eq("id", transferId).single();
  if (t.error) throw t.error;
  const transfer = t.data;

  const props = await supabaseAdmin.from("properties").select("id, owner_id").in("id", transfer.property_ids);
  if (props.error) throw props.error;
  const properties = props.data ?? [];

  const summary: any = { propertiesAffected: properties.length, dryRun, doorloopCalls: 0 };

  // ECC DB updates
  if (!dryRun) {
    const { error } = await supabaseAdmin
      .from("properties")
      .update({ owner_id: transfer.new_owner_id })
      .in("id", transfer.property_ids);
    if (error) throw error;
  }

  // DoorLoop write-back (if configured)
  if (!dryRun && process.env.DOORLOOP_API_KEY && process.env.DOORLOOP_BASE_URL) {
    const headers = {
      "Authorization": `Bearer ${process.env.DOORLOOP_API_KEY}`,
      "Content-Type": "application/json",
      "Idempotency-Key": `transfer-${transferId}-${transfer.effective_date}`,
    };

    for (const p of properties) {
      try {
        const url = `${process.env.DOORLOOP_BASE_URL}/properties/${p.id}`;
        const res = await fetch(url, { 
          method: "PUT", 
          headers, 
          body: JSON.stringify({ ownerId: transfer.new_owner_id }) 
        });

        await auditEvent({ 
          event_type: "OWNER_TRANSFER_WRITEBACK", 
          ref_table: "properties", 
          ref_id: p.id, 
          payload: { status: res.status } 
        });

        summary.doorloopCalls++;

        if (!res.ok) {
          const text = await res.text().catch(() => "");
          throw new Error(`DoorLoop property update failed: ${res.status} ${text}`);
        }
      } catch (err) {
        console.error(`DoorLoop writeback failed for property ${p.id}:`, err);
        // Continue with other properties even if one fails
      }
    }
  }

  // Mark transfer complete
  if (!dryRun) {
    const { error } = await supabaseAdmin
      .from("owner_transfers")
      .update({ 
        status: "COMPLETE", 
        executed_at: new Date().toISOString() 
      })
      .eq("id", transferId);
    if (error) throw error;

    await auditEvent({ 
      event_type: "OWNER_TRANSFER_COMPLETE", 
      ref_table: "owner_transfers", 
      ref_id: transferId, 
      payload: summary 
    });
  }

  return { applied: !dryRun, summary };
}

export async function getTransferDetails(transferId: number) {
  // Get transfer record
  const t = await supabaseAdmin.from("owner_transfers").select("*").eq("id", transferId).single();
  if (t.error) throw t.error;

  // Get audit events for this transfer
  const auditQuery = await supabaseAdmin
    .from("audit_events")
    .select("*")
    .eq("ref_table", "owner_transfers")
    .eq("ref_id", transferId)
    .order("created_at", { ascending: false });

  return {
    transfer: t.data,
    auditEvents: auditQuery.data ?? [],
  };
}

