import { sbAdmin } from "../lib/supabaseAdmin";
import { geocodeAddress } from "../lib/geocode/geocode";
import { recordAudit } from "../lib/audit";

async function getState(key: string) {
  const { data } = await sbAdmin.from("integration_state").select("value").eq("key", key).maybeSingle();
  return data?.value ?? null;
}

async function setState(key: string, value: any) {
  await sbAdmin.from("integration_state").upsert({ key, value });
}

export async function runAutoGeocodeTick() {
  // prefer integration_state override, else env, else 10
  const overrideBatch = (await getState("GEOCODE_TICK_BATCH")) ?? null;
  const BATCH = Number.isFinite(Number(overrideBatch)) ? Number(overrideBatch) : parseInt(process.env.GEOCODE_TICK_BATCH || "10", 10);

  const { data: props, error } = await sbAdmin
    .from("properties")
    .select("id,line1,line2,city,state,postal_code,lat,lng")
    .or("lat.is.null,lng.is.null")
    .limit(BATCH);

  if (error) return;

  let done = 0;
  const processed = (props || []).length;

  for (const p of (props || [])) {
    try {
      const res = await geocodeAddress({ 
        line1: p.line1, 
        line2: p.line2, 
        city: p.city, 
        state: p.state, 
        postal_code: p.postal_code 
      });

      if (res) {
        const { error: upErr } = await sbAdmin
          .from("properties")
          .update({ lat: res.lat, lng: res.lng })
          .eq("id", p.id);

        if (!upErr) {
          await recordAudit({ 
            event_type: "GEOCODE_AUTO_APPLY", 
            label: "GEOCODE_AUTO", 
            ref_table: "properties", 
            ref_id: p.id, 
            payload: res 
          });
          done++;
        }
      }
    } catch { 
      /* swallow batch errors */ 
    }
  }

  // Update state with run statistics
  await setState("GEOCODE_LAST_RUN", { 
    ts: new Date().toISOString(), 
    processed, 
    updated: done,
    batch_size: BATCH
  });
}

server/lib/geocode/geocode.ts.content:
import fetch from "node-fetch";
import crypto from "crypto";
import { sbAdmin } from "../supabaseAdmin";
import { recordAudit } from "../audit";
const PROVIDER = process.env.GEOCODER_PROVIDER || "google";
const API_KEY = process.env.GOOGLE_MAPS_API_KEY || "";

function norm(s: string) {
  return s.trim().replace(/\s+/g, " ").toUpperCase();
}
function addrKey(addr: { line1?: string; line2?: string; city?: string; state?: string; postal_code?: string }) {
  const base = [addr.line1, addr.line2, addr.city, addr.state, addr.postal_code].filter(Boolean).map(norm).join(", ");
  return crypto.createHash("sha256").update(base).digest("hex");
}

async function getFromCache(hash: string) {
  const { data, error } = await sbAdmin
    .from("geocode_cache")
    .select("lat,lng,normalized")
    .eq("address_hash", hash)
    .maybeSingle();
  if (error) throw error;
  return data || null;
}
async function putInCache(hash: string, normalized: string, lat: number, lng: number) {
  const { error } = await sbAdmin.from("geocode_cache").upsert({ address_hash: hash, normalized, lat, lng });
  if (error) throw error;
}

async function googleGeocode(q: string): Promise<{ lat: number; lng: number; normalized: string } | null> {
  if (!API_KEY) return null;
  const url = `https://maps.googleapis.com/maps/api/geocode/json?address=${encodeURIComponent(q)}&key=${API_KEY}`;
  const res = await fetch(url);
  if (!res.ok) return null;
  const j = await res.json();
  const hit = j?.results?.[0];
  if (!hit) return null;
  const lat = hit.geometry?.location?.lat;
  const lng = hit.geometry?.location?.lng;
  const normalized = hit.formatted_address || q;
  if (typeof lat !== "number" || typeof lng !== "number") return null;
  return { lat, lng, normalized };
}

// very light 2 rps governor
let last = 0;
async function throttle() {
  const now = Date.now();
  const delta = now - last;
  const minGap = 500; // ms => 2 req/sec
  if (delta < minGap) await new Promise(r => setTimeout(r, minGap - delta));
  last = Date.now();
}

export type Address = { line1?: string; line2?: string; city?: string; state?: string; postal_code?: string };

export async function geocodeAddress(addr: Address): Promise<{ lat: number; lng: number } | null> {
  const parts = [addr.line1, addr.line2, addr.city, addr.state, addr.postal_code].filter(Boolean);
  if (!parts.length) return null;
  const query = parts.join(", ");
  const hash = addrKey(addr);

  // cache
  const cached = await getFromCache(hash);
  if (cached) return { lat: cached.lat, lng: cached.lng };

  await throttle();
  let result: { lat: number; lng: number; normalized: string } | null = null;
  if (PROVIDER === "google") result = await googleGeocode(query);

  if (result) {
    await putInCache(hash, result.normalized, result.lat, result.lng);
    await recordAudit({
      event_type: "GEOCODE_AUTO",
      label: "GEOCODE_AUTO",
      ref_table: "properties",
      payload: { provider: PROVIDER, query, normalized: result.normalized }
    });
    return { lat: result.lat, lng: result.lng };
  }
  return null;
}

server/lib/ownerTransfer.ts.content:
import { sbAdmin } from "./supabaseAdmin";

type TransferStatus = 'DRAFT'|'PENDING_ACCOUNTING'|'APPROVED_ACCOUNTING'|'READY_EXECUTION'|'COMPLETE'|'FAILED';

export type InitiatePayload = {
  old_owner_id: number;
  new_owner_id: number;
  property_ids: number[];
  effective_date: string; // "YYYY-MM-DD"
  notes?: string;
  initiated_by?: string; // UUID (optional)
};

const nowIso = () => new Date().toISOString();

async function insertAudit(event_type:string, payload:any, ref_table?:string, ref_id?:number, label?:string) {
  try {
    await sbAdmin.from("audit_events").insert({
      event_type, ref_table, ref_id, payload, label, created_at: new Date().toISOString()
    });
  } catch {}
}

async function safeSingle<T=any>(q:any):Promise<T|null> {
  const { data, error } = await q.single();
  if (error?.code === "PGRST116") return null;
  if (error) throw new Error(error.message);
  return data as T;
}

export async function validatePropsBelongToOwner(property_ids:number[], owner_id:number) {
  if (!property_ids.length) throw new Error("No properties selected.");
  const { data, error } = await sbAdmin
    .from("properties")
    .select("id")
    .eq("owner_id", owner_id)
    .in("id", property_ids);
  if (error) throw new Error(error.message);
  const okIds = new Set((data||[]).map((r:any)=>r.id));
  const bad = property_ids.filter(id=>!okIds.has(id));
  if (bad.length) throw new Error(`Some properties are not owned by owner ${owner_id}: [${bad.join(", ")}]`);
}

export async function snapshotForTransfer(transfer_id:number, property_ids:number[]) {
  // Snapshot properties
  const tables = [
    { name: "properties", key: "id", filter: "id" },
    { name: "units", key: "id", filter: "property_id" },
    { name: "leases", key: "id", filter: "property_id" },
    { name: "tenants", key: "id", filter: "property_id" },
  ];
  for (const t of tables) {
    try {
      const { data } = await sbAdmin.from(t.name).select("*").in(t.filter, property_ids);
      for (const row of (data||[])) {
        await sbAdmin.from("owner_transfer_snapshots").insert({
          transfer_id,
          entity_type: t.name === "properties" ? "property"
                     : t.name === "units" ? "unit"
                     : t.name === "leases" ? "lease"
                     : "tenant",
          entity_id: row[t.key],
          raw_jsonb: row,
          captured_at: nowIso(),
        });
      }
    } catch {}
  }
}

export async function initiateTransfer(p:InitiatePayload) {
  if (p.old_owner_id === p.new_owner_id) throw new Error("New owner must be different.");
  await validatePropsBelongToOwner(p.property_ids, p.old_owner_id);

  const today = new Date(); today.setHours(0,0,0,0);
  const eff = new Date(p.effective_date); eff.setHours(0,0,0,0);

  // business rule: always go through Accounting first
  const status: TransferStatus = 'PENDING_ACCOUNTING';

  const { data, error } = await sbAdmin.from("owner_transfers").insert({
    property_ids: p.property_ids,
    old_owner_id: p.old_owner_id,
    new_owner_id: p.new_owner_id,
    effective_date: p.effective_date,
    status,
    notes: p.notes || null,
    initiated_by: p.initiated_by || null,
    created_at: nowIso(),
  }).select("id").single();

  if (error) throw new Error(error.message);
  const transfer_id = data.id as number;

  await snapshotForTransfer(transfer_id, p.property_ids);
  await insertAudit("OWNER_TRANSFER_INITIATED", { ...p, transfer_id, status, timing: eff.getTime() < today.getTime() ? "retro" : "future" }, "owner_transfers", transfer_id, "OWNER_TRANSFER");

  return { transfer_id, status };
}

export async function approveAccounting(transfer_id:number) {
  await sbAdmin.from("owner_transfers").update({ status: 'APPROVED_ACCOUNTING' }).eq("id", transfer_id);
  await insertAudit("OWNER_TRANSFER_APPROVED_ACCOUNTING", { transfer_id }, "owner_transfers", transfer_id, "OWNER_TRANSFER");
  return { ok: true };
}

export async function authorizeTransfer(transfer_id:number) {
  await sbAdmin.from("owner_transfers").update({ status: 'READY_EXECUTION' }).eq("id", transfer_id);
  await insertAudit("OWNER_TRANSFER_READY_EXECUTION", { transfer_id }, "owner_transfers", transfer_id, "OWNER_TRANSFER");
  return { ok: true };
}

export async function executeTransfer(transfer_id:number) {
  const t = await safeSingle<any>(sbAdmin.from("owner_transfers").select("*").eq("id", transfer_id));
  if (!t) throw new Error("Transfer not found.");
  if (t.status !== 'READY_EXECUTION') throw new Error(`Transfer not ready (status=${t.status}).`);

  // Change ownership on selected properties
  const { error } = await sbAdmin.from("properties")
    .update({ owner_id: t.new_owner_id })
    .in("id", t.property_ids || []);
  if (error) throw new Error(error.message);

  await sbAdmin.from("owner_transfers").update({ status: 'COMPLETE', executed_at: nowIso() }).eq("id", transfer_id);
  await insertAudit("OWNER_TRANSFER_EXECUTED", { transfer_id, property_ids: t.property_ids }, "owner_transfers", transfer_id, "OWNER_TRANSFER");
  return { ok: true };
}

/** tiny scheduler tick: execute any READY_EXECUTION with effective_date <= today */
export async function runDueTransfersTick() {
  const today = new Date(); today.setHours(23,59,59,999);
  const { data, error } = await sbAdmin
    .from("owner_transfers")
    .select("id")
    .lte("effective_date", today.toISOString().slice(0,10))
    .eq("status", "READY_EXECUTION")
    .limit(20);
  if (error) return;

  for (const row of (data || [])) {
    try { await executeTransfer(row.id); } catch {}
  }
}

server/lib/sync/auto.ts.content:
// server/lib/sync/auto.ts
import { sbAdmin } from "../supabaseAdmin";
import { runSync } from "./index";
import { upsertState, getState } from "../integrationState";

const S_LOCK = "AUTO_SYNC_LOCK";
const S_STATUS = "AUTO_SYNC_STATUS";
const S_LAST_FULL = "doorloop_last_full";
const S_LAST_INCR = "doorloop_last_incr";

function env(name: string, fallback?: string) {
  return (process.env[name] ?? fallback ?? "").trim();
}

function nowIso() {
  return new Date().toISOString();
}

async function withDbLock<T>(ttlMinutes: number, fn: () => Promise<T>): Promise<T | null> {
  const startedAt = nowIso();
  const expiresAt = new Date(Date.now() + ttlMinutes * 60 * 1000).toISOString();
  const holder = `auto-sync-${Date.now()}`;

  // Attempt to acquire lock if not held or expired
  const current = await getState(S_LOCK);
  const okToLock = (() => {
    if (!current?.value) return true;
    const v = current.value as any;
    if (!v.locked) return true;
    const expiry = v.expires_at ? Date.parse(v.expires_at) : 0;
    return Date.now() > expiry; // expired
  })();

  if (!okToLock) return null;

  // Acquire lock with TTL
  await upsertState(S_LOCK, { 
    locked: true, 
    holder, 
    expires_at: expiresAt,
    acquired_at: startedAt 
  });

  try {
    const result = await fn();
    return result;
  } finally {
    // Release lock
    await upsertState(S_LOCK, { 
      locked: false, 
      holder: null, 
      expires_at: null,
      released_at: nowIso() 
    });
  }
}

async function audit(event_type: string, payload: any) {
  // Best-effort audit, don't throw
  try {
    await sbAdmin
      .from("audit_events")
      .insert({
        event_type,
        ref_table: "sync",
        payload,
        created_at: new Date().toISOString(),
        // leave actor_id null â€” this is system
        // add a label to filter later if you want:
        label: "AUTO_SYNC",
      })
      .throwOnError();
  } catch {}
}

function parseEntityList(s: string): string[] {
  return s
    .split(",")
    .map((t) => t.trim())
    .filter(Boolean);
}

function shouldDoNightlyFull(nowUtc: Date, hourStr: string | undefined, lastFullIso?: string): boolean {
  if (!hourStr) return false;
  const targetHour = Number(hourStr);
  if (!Number.isFinite(targetHour)) return false;

  const lastFullDay = lastFullIso ? new Date(lastFullIso).toISOString().slice(0, 10) : "";
  const today = nowUtc.toISOString().slice(0, 10);
  const isNewDay = today !== lastFullDay;
  const isTargetHour = nowUtc.getUTCHours() === targetHour;
  return isNewDay && isTargetHour;
}

export function startAutoSyncLoop() {
  const enabled = env("AUTO_SYNC_ENABLED", "false").toLowerCase() === "true";
  const apiKeyPresent = !!env("DOORLOOP_API_KEY");

  const intervalMin = Math.max(2, Number(env("AUTO_SYNC_INTERVAL_MINUTES", "10")));
  const fullAtHour = env("AUTO_SYNC_FULL_AT_HOUR_UTC", ""); 
  const entities = parseEntityList(env("SYNC_ENTITIES", "owners,properties,units,leases,tenants"));
  const lockTtlMin = Math.max(5, Number(env("AUTO_SYNC_LOCK_TTL_MINUTES", "20")));
  const hardDeadlineMin = Math.max(10, Number(env("AUTO_SYNC_HARD_DEADLINE_MINUTES", "30")));

  if (!enabled) {
    console.log("[auto-sync] disabled via AUTO_SYNC_ENABLED");
    return;
  }

  const tick = async () => {
    const now = nowIso();
    const nextRunAt = new Date(Date.now() + intervalMin * 60 * 1000).toISOString();

    try {
      // Update status - always track that we attempted a run
      await upsertState(S_STATUS, {
        last_run_at: now,
        next_run_at: nextRunAt,
        mode: "pending"
      });

      if (!apiKeyPresent) {
        console.log("[auto-sync] DOORLOOP_API_KEY missing; scheduling next tick");
        await audit("AUTO_SYNC_TICK", { 
          status: "skipped", 
          reason: "missing_api_key",
          next_run_at: nextRunAt 
        });
        return;
      }

      // Check for catch-up scenario
      const status = (await getState(S_STATUS))?.value;
      const lastSuccess = status?.last_success_at;
      const needsCatchUp = lastSuccess && 
        (Date.now() - Date.parse(lastSuccess)) > (hardDeadlineMin * 60 * 1000);

      if (needsCatchUp) {
        console.log("[auto-sync] catch-up needed - last success too old");
        await audit("AUTO_SYNC_CATCHUP", { 
          last_success_at: lastSuccess,
          deadline_minutes: hardDeadlineMin
        });
      }

      // Decide mode: nightly full or incremental
      const lastFull = (await getState(S_LAST_FULL))?.value?.when as string | undefined;
      const doFull = shouldDoNightlyFull(new Date(), fullAtHour || undefined, lastFull);
      const mode = doFull ? "full" : "incremental";

      const ran = await withDbLock(lockTtlMin, async () => {
        const started_at = nowIso();

        // Update status to running
        await upsertState(S_STATUS, {
          last_run_at: now,
          next_run_at: nextRunAt,
          mode: mode,
          status: "running",
          started_at
        });

        await audit("AUTO_SYNC_STARTED", { mode, entities, started_at });

        const result = await runSync(entities as any[], mode as "full" | "incremental");

        const finished_at = nowIso();
        await audit("AUTO_SYNC_FINISHED", {
          mode,
          entities,
          started_at,
          finished_at,
          result,
        });

        // Update success status and checkpoints
        await upsertState(S_STATUS, {
          last_run_at: now,
          last_success_at: finished_at,
          next_run_at: nextRunAt,
          mode: mode,
          status: "completed"
        });

        if (mode === "full") {
          await upsertState(S_LAST_FULL, { when: finished_at });
        } else {
          await upsertState(S_LAST_INCR, { when: finished_at });
        }
        return result;
      });

      if (!ran) {
        console.log("[auto-sync] skipped â€” lock held");
        await audit("AUTO_SYNC_TICK", { 
          status: "skipped", 
          reason: "lock_held",
          next_run_at: nextRunAt 
        });
      }
    } catch (err: any) {
      console.error("[auto-sync] error:", err?.message || err);
      await audit("AUTO_SYNC_ERROR", { 
        message: String(err?.message || err),
        next_run_at: nextRunAt
      });

      // Update status to show error
      await upsertState(S_STATUS, {
        last_run_at: now,
        next_run_at: nextRunAt,
        mode: "error",
        status: "failed",
        error: String(err?.message || err)
      });
    }
  };

  // initial small delay to avoid clashing with boot
  setTimeout(tick, 2500);
  // steady state loop
  setInterval(tick, intervalMin * 60 * 1000);
  console.log(`[auto-sync] enabled every ${intervalMin}m; lock TTL: ${lockTtlMin}m; deadline: ${hardDeadlineMin}m; nightly full at UTC hour: ${fullAtHour || "disabled"}`);
}

