import React, { useState, useEffect } from "react";
import { useLocation } from "wouter";

interface Transfer {
  id: number;
  status: string;
  old_owner_id: number;
  new_owner_id: number;
  property_ids: number[];
  effective_date: string;
  notes?: string;
  created_at: string;
}

// Mock user role - in real app this would come from auth context
const getUserRole = () => {
  // For demo purposes, return 'admin' - in real app this would come from auth
  return 'admin'; // or 'user', 'accounting', etc.
};

const isAdmin = () => getUserRole() === 'admin';
const isAccounting = () => getUserRole() === 'accounting' || isAdmin();

export default function TransferDetailPage() {
  const [location] = useLocation();
  const params = new URLSearchParams(location.split('?')[1]);
  const transferId = params.get('id');

  const [transfer, setTransfer] = useState<Transfer | null>(null);
  const [loading, setLoading] = useState(true);
  const [actionLoading, setActionLoading] = useState<string | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [toast, setToast] = useState<{ message: string; type: 'success' | 'error' } | null>(null);

  // Load transfer details
  const loadTransfer = async () => {
    if (!transferId) return;

    setLoading(true);
    try {
      const response = await fetch(`/api/owner-transfer/${transferId}`);
      const data = await response.json();

      if (data.ok) {
        setTransfer(data.transfer);
      } else {
        setError(data.error || 'Failed to load transfer');
      }
    } catch (err) {
      setError('Failed to load transfer details');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadTransfer();
  }, [transferId]);

  // Show toast notification
  const showToast = (message: string, type: 'success' | 'error') => {
    setToast({ message, type });
    setTimeout(() => setToast(null), 3000);
  };

  // Execute admin action
  const executeAction = async (action: string, endpoint: string) => {
    if (!transferId) return;

    setActionLoading(action);
    try {
      const response = await fetch(endpoint, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ transfer_id: parseInt(transferId) })
      });

      const data = await response.json();

      if (data.ok) {
        showToast(`${action} completed successfully`, 'success');
        await loadTransfer(); // Refresh transfer data
      } else {
        showToast(data.error || `${action} failed`, 'error');
      }
    } catch (err) {
      showToast(`${action} failed`, 'error');
    } finally {
      setActionLoading(null);
    }
  };

  // Get button state for each action
  const getButtonState = (action: string) => {
    if (!transfer) return { disabled: true, reason: 'Loading...' };

    switch (action) {
      case 'approve-accounting':
        if (!isAccounting()) return { disabled: true, reason: 'Requires accounting role' };
        if (transfer.status !== 'PENDING_ACCOUNTING') return { disabled: true, reason: 'Not pending accounting approval' };
        return { disabled: false, reason: '' };

      case 'authorize':
        if (!isAdmin()) return { disabled: true, reason: 'Requires admin role' };
        if (transfer.status !== 'APPROVED_ACCOUNTING') return { disabled: true, reason: 'Must be approved by accounting first' };
        return { disabled: false, reason: '' };

      case 'execute':
        if (!isAdmin()) return { disabled: true, reason: 'Requires admin role' };
        if (transfer.status !== 'READY_EXECUTION') return { disabled: true, reason: 'Not ready for execution' };
        return { disabled: false, reason: '' };

      default:
        return { disabled: true, reason: 'Unknown action' };
    }
  };

  const approveAccountingState = getButtonState('approve-accounting');
  const authorizeState = getButtonState('authorize');
  const executeState = getButtonState('execute');

  if (loading) {
    return (
      <div className="p-8">
        <div className="text-center text-muted-foreground">Loading transfer details...</div>
      </div>
    );
  }

  if (error || !transfer) {
    return (
      <div className="p-8">
        <div className="text-center text-destructive">{error || 'Transfer not found'}</div>
      </div>
    );
  }

  return (
    <div className="p-8">
      {/* Toast Notification */}
      {toast && (
        <div className={`fixed top-4 right-4 p-4 rounded-md shadow-md z-50 ${
          toast.type === 'success' ? 'bg-green-500 text-white' : 'bg-red-500 text-white'
        }`}>
          {toast.message}
        </div>
      )}

      <h1 className="text-2xl font-semibold mb-6">Transfer Details</h1>

      <div className="space-y-6">
        {/* Transfer Information */}
        <div className="bg-secondary border border-border rounded-lg p-6">
          <h2 className="text-lg font-medium mb-4">Transfer #{transfer.id}</h2>

          <div className="grid grid-cols-2 gap-4 text-sm">
            <div>
              <div className="text-muted-foreground">Status</div>
              <div className="font-medium">{transfer.status}</div>
            </div>
            <div>
              <div className="text-muted-foreground">Effective Date</div>
              <div className="font-medium">{transfer.effective_date}</div>
            </div>
            <div>
              <div className="text-muted-foreground">Old Owner ID</div>
              <div className="font-medium">{transfer.old_owner_id}</div>
            </div>
            <div>
              <div className="text-muted-foreground">New Owner ID</div>
              <div className="font-medium">{transfer.new_owner_id}</div>
            </div>
            <div>
              <div className="text-muted-foreground">Properties</div>
              <div className="font-medium">{transfer.property_ids?.length || 0} properties</div>
            </div>
            <div>
              <div className="text-muted-foreground">Created</div>
              <div className="font-medium">{new Date(transfer.created_at).toLocaleDateString()}</div>
            </div>
          </div>

          {transfer.notes && (
            <div className="mt-4">
              <div className="text-muted-foreground text-sm">Notes</div>
              <div className="mt-1">{transfer.notes}</div>
            </div>
          )}
        </div>

        {/* Admin Actions */}
        <div className="bg-secondary border border-border rounded-lg p-6">
          <h2 className="text-lg font-medium mb-4">Admin Actions</h2>

          <div className="flex gap-4">
            {/* Approve Accounting Button */}
            <div className="relative">
              <button
                onClick={() => executeAction('Approve Accounting', '/api/owner-transfer/approve-accounting')}
                disabled={approveAccountingState.disabled || actionLoading === 'Approve Accounting'}
                className={`px-4 py-2 rounded-md font-medium transition-colors ${
                  approveAccountingState.disabled 
                    ? 'bg-muted text-muted-foreground cursor-not-allowed' 
                    : 'bg-blue-600 text-white hover:bg-blue-700'
                }`}
                data-testid="approve-accounting-btn"
                title={approveAccountingState.reason}
              >
                {actionLoading === 'Approve Accounting' ? 'Approving...' : 'Approve Accounting'}
              </button>
              {approveAccountingState.disabled && approveAccountingState.reason && (
                <div className="absolute bottom-full mb-2 left-0 bg-black text-white text-xs px-2 py-1 rounded whitespace-nowrap opacity-0 group-hover:opacity-100 transition-opacity">
                  {approveAccountingState.reason}
                </div>
              )}
            </div>

            {/* Authorize Button */}
            <div className="relative group">
              <button
                onClick={() => executeAction('Authorize', '/api/owner-transfer/authorize')}
                disabled={authorizeState.disabled || actionLoading === 'Authorize'}
                className={`px-4 py-2 rounded-md font-medium transition-colors ${
                  authorizeState.disabled 
                    ? 'bg-muted text-muted-foreground cursor-not-allowed' 
                    : 'bg-green-600 text-white hover:bg-green-700'
                }`}
                data-testid="authorize-btn"
                title={authorizeState.reason}
              >
                {actionLoading === 'Authorize' ? 'Authorizing...' : 'Authorize'}
              </button>
              {authorizeState.disabled && authorizeState.reason && (
                <div className="absolute bottom-full mb-2 left-0 bg-black text-white text-xs px-2 py-1 rounded whitespace-nowrap opacity-0 group-hover:opacity-100 transition-opacity">
                  {authorizeState.reason}
                </div>
              )}
            </div>

            {/* Execute Now Button */}
            <div className="relative group">
              <button
                onClick={() => executeAction('Execute Now', '/api/owner-transfer/execute')}
                disabled={executeState.disabled || actionLoading === 'Execute Now'}
                className={`px-4 py-2 rounded-md font-medium transition-colors ${
                  executeState.disabled 
                    ? 'bg-muted text-muted-foreground cursor-not-allowed' 
                    : 'bg-red-600 text-white hover:bg-red-700'
                }`}
                data-testid="execute-now-btn"
                title={executeState.reason}
              >
                {actionLoading === 'Execute Now' ? 'Executing...' : 'Execute Now'}
              </button>
              {executeState.disabled && executeState.reason && (
                <div className="absolute bottom-full mb-2 left-0 bg-black text-white text-xs px-2 py-1 rounded whitespace-nowrap opacity-0 group-hover:opacity-100 transition-opacity">
                  {executeState.reason}
                </div>
              )}
            </div>
          </div>

          <div className="mt-4 text-sm text-muted-foreground">
            User Role: <span className="font-medium">{getUserRole()}</span>
          </div>
        </div>
      </div>
    </div>
  );
}

