import { X, AlertCircle, Clock, Database } from "lucide-react";

interface ErrorPaneProps {
  isOpen: boolean;
  onClose: () => void;
  runDetails: {
    id: string;
    timestamp: string;
    entity: string;
    mode: "delta" | "backfill";
    rows: number;
    duration: number;
    result: "success" | "error" | "warning";
    error?: string;
    fullMessage?: string;
    responseBody?: string;
    fullDetails?: any;
  } | null;
}

const formatTimestamp = (timestamp: string) => {
  return new Date(timestamp).toLocaleString();
};

const formatDuration = (ms: number) => {
  if (ms < 1000) return `${ms}ms`;
  if (ms < 60000) return `${(ms / 1000).toFixed(1)}s`;
  return `${(ms / 60000).toFixed(1)}m`;
};

export default function ErrorPane({ isOpen, onClose, runDetails }: ErrorPaneProps) {
  if (!isOpen || !runDetails) return null;

  return (
    <section className="ecc-object" role="region" aria-label="Integration Error Details">
      <div className="ecc-header">
        <div>
          <div className="ecc-title">Sync Run Details</div>
          <p className="text-sm text-gray-600 dark:text-gray-400">
            {formatTimestamp(runDetails.timestamp)}
          </p>
        </div>
        <button
          onClick={onClose}
          className="ecc-object"
          style={{ padding: "8px 12px" }}
          data-testid="button-close-error-pane"
        >
          <X className="w-5 h-5" />
        </button>
      </div>

      {/* Content */}
      <div className="flex-1 overflow-y-auto p-6 space-y-6">
        {/* Summary */}
        <div className="ecc-object">
          <div className="ecc-header">
            <h3 className="ecc-title">Summary</h3>
          </div>
          <div className="ecc-card__body space-y-3">
            <div className="ecc-field-row">
              <span className="ecc-label">Entity:</span>
              <span className="ecc-badge ecc-badge--secondary capitalize">
                {runDetails.entity}
              </span>
            </div>
            <div className="ecc-field-row">
              <span className="ecc-label">Mode:</span>
              <span className={`ecc-badge ${
                runDetails.mode === 'backfill' 
                  ? 'ecc-badge--warning' 
                  : 'ecc-badge--info'
              }`}>
                {runDetails.mode}
              </span>
            </div>
            <div className="ecc-field-row">
              <span className="ecc-label">Rows Processed:</span>
              <span className="ecc-field-value font-mono">
                {runDetails.rows.toLocaleString()}
              </span>
            </div>
            <div className="ecc-field-row">
              <span className="ecc-label">Duration:</span>
              <span className="ecc-field-value font-mono">
                {formatDuration(runDetails.duration)}
              </span>
            </div>
            <div className="ecc-field-row">
              <span className="ecc-label">Result:</span>
              <span className={`ecc-badge ${
                runDetails.result === 'success' ? 'ecc-badge--success' :
                runDetails.result === 'error' ? 'ecc-badge--error' :
                'ecc-badge--warning'
              }`}>
                {runDetails.result}
              </span>
            </div>
          </div>
        </div>

        {/* Error Message */}
        {runDetails.error && (
          <div className="ecc-object">
            <div className="ecc-header">
              <h3 className="ecc-title flex items-center space-x-2">
                <AlertCircle className="w-5 h-5 text-red-500" />
                <span>Error Summary</span>
              </h3>
            </div>
            <div className="ecc-card__body">
              <pre className="text-sm text-red-600 dark:text-red-400 whitespace-pre-wrap font-mono bg-red-50 dark:bg-red-950 p-3 rounded">
                {runDetails.error}
              </pre>
            </div>
          </div>
        )}

        {/* Full Error Message */}
        {runDetails.fullMessage && runDetails.fullMessage !== runDetails.error && (
          <div className="ecc-object">
            <div className="ecc-header">
              <h3 className="ecc-title">Full Error Message</h3>
            </div>
            <div className="ecc-card__body">
              <pre className="text-sm whitespace-pre-wrap font-mono bg-gray-50 dark:bg-gray-800 p-3 rounded max-h-64 overflow-y-auto">
                {runDetails.fullMessage}
              </pre>
            </div>
          </div>
        )}

        {/* Response Body */}
        {runDetails.responseBody && (
          <div className="ecc-object">
            <div className="ecc-header">
              <h3 className="ecc-title">Response Body (First 2KB)</h3>
            </div>
            <div className="ecc-card__body">
              <pre className="text-sm whitespace-pre-wrap font-mono bg-gray-50 dark:bg-gray-800 p-3 rounded max-h-64 overflow-y-auto">
                {runDetails.responseBody}
              </pre>
            </div>
          </div>
        )}

        {/* Full Details */}
        {runDetails.fullDetails && (
          <div className="ecc-object">
            <div className="ecc-header">
              <h3 className="ecc-title">Raw Details</h3>
            </div>
            <div className="ecc-card__body">
              <pre className="text-sm whitespace-pre-wrap font-mono bg-gray-50 dark:bg-gray-800 p-3 rounded max-h-96 overflow-y-auto">
                {JSON.stringify(runDetails.fullDetails, null, 2)}
              </pre>
            </div>
          </div>
        )}
      </div>
    </section>
  );
}

import { useState } from "react";
import { CheckCircle, XCircle, Clock, AlertCircle } from "lucide-react";

interface SyncRun {
  id: string;
  timestamp: string;
  entity: string;
  mode: "delta" | "backfill";
  rows: number;
  duration: number;
  result: "success" | "error" | "warning";
  error?: string;
  fullDetails?: any;
}

interface LastRunTableProps {
  runs: SyncRun[];
  onRowClick: (run: SyncRun) => void;
}

const ResultIcon = ({ result }: { result: SyncRun['result'] }) => {
  const config = {
    success: { icon: CheckCircle, className: "text-green-500" },
    error: { icon: XCircle, className: "text-red-500" },
    warning: { icon: AlertCircle, className: "text-yellow-500" },
  };

  const { icon: Icon, className } = config[result];
  return <Icon className={`w-4 h-4 ${className}`} />;
};

const formatDuration = (ms: number) => {
  if (ms < 1000) return `${ms}ms`;
  if (ms < 60000) return `${(ms / 1000).toFixed(1)}s`;
  return `${(ms / 60000).toFixed(1)}m`;
};

const formatTimestamp = (timestamp: string) => {
  return new Date(timestamp).toLocaleString();
};

export default function LastRunTable({ runs, onRowClick }: LastRunTableProps) {
  return (
    <div className="ecc-card">
      <div className="ecc-card__header">
        <h3 className="ecc-card__title">Last 20 Runs</h3>
      </div>

      <div className="ecc-card__body">
        {runs.length === 0 ? (
          <div className="text-center py-8">
            <Clock className="w-8 h-8 mx-auto mb-2 text-gray-400" />
            <p className="text-gray-500">No sync runs yet</p>
          </div>
        ) : (
          <div className="overflow-x-auto">
            <table className="ecc-table w-full">
              <thead>
                <tr>
                  <th className="ecc-table__header">Time</th>
                  <th className="ecc-table__header">Entity</th>
                  <th className="ecc-table__header">Mode</th>
                  <th className="ecc-table__header">Rows</th>
                  <th className="ecc-table__header">Duration</th>
                  <th className="ecc-table__header">Result</th>
                  <th className="ecc-table__header">Error</th>
                </tr>
              </thead>
              <tbody>
                {runs.map((run) => (
                  <tr
                    key={run.id}
                    className="ecc-table__row cursor-pointer hover:bg-gray-50 dark:hover:bg-gray-800"
                    onClick={() => onRowClick(run)}
                    data-testid={`row-sync-run-${run.id}`}
                  >
                    <td className="ecc-table__cell">
                      <span className="text-sm font-mono">
                        {formatTimestamp(run.timestamp)}
                      </span>
                    </td>
                    <td className="ecc-table__cell">
                      <span className="ecc-badge ecc-badge--secondary capitalize">
                        {run.entity}
                      </span>
                    </td>
                    <td className="ecc-table__cell">
                      <span className={`ecc-badge ${
                        run.mode === 'backfill' 
                          ? 'ecc-badge--warning' 
                          : 'ecc-badge--info'
                      }`}>
                        {run.mode}
                      </span>
                    </td>
                    <td className="ecc-table__cell">
                      <span className="font-mono text-sm">
                        {run.rows.toLocaleString()}
                      </span>
                    </td>
                    <td className="ecc-table__cell">
                      <span className="font-mono text-sm">
                        {formatDuration(run.duration)}
                      </span>
                    </td>
                    <td className="ecc-table__cell">
                      <div className="flex items-center space-x-1">
                        <ResultIcon result={run.result} />
                        <span className="capitalize">{run.result}</span>
                      </div>
                    </td>
                    <td className="ecc-table__cell">
                      {run.error ? (
                        <span className="text-red-400 text-sm truncate max-w-xs">
                          {run.error}
                        </span>
                      ) : (
                        <span className="text-gray-400">â€”</span>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
}

import { useState } from "react";
import { Play, Square, RefreshCw, Database, TestTube, Calendar, BarChart3, Clock, Activity } from "lucide-react";

interface ProgressInfo {
  entity: string;
  page: number;
  rows: number;
  totalPages?: number;
  completedPages?: number;
  rps?: number; // Rows per second
  eta?: number; // Estimated time to completion in milliseconds
  startTime?: number;
}

interface RunOptions {
  mode: "delta" | "backfill";
  dryRun?: boolean;
  since?: string; // ISO string for delta mode
}

interface RunPanelProps {
  entities: string[];
  selectedEntities: string[];
  onEntitiesChange: (entities: string[]) => void;
  isRunning: boolean;
  currentProgress?: ProgressInfo;
  onRun: (options: RunOptions) => Promise<void>;
  onStop: () => Promise<void>;
}

// Utility functions
const formatETA = (etaMs: number) => {
  if (etaMs < 60000) return `${Math.ceil(etaMs / 1000)}s`;
  if (etaMs < 3600000) return `${Math.ceil(etaMs / 60000)}m`;
  return `${Math.ceil(etaMs / 3600000)}h`;
};

const formatRPS = (rps: number) => {
  if (rps < 1) return `${(rps * 1000).toFixed(0)} rows/s`;
  if (rps < 1000) return `${rps.toFixed(1)} rows/s`;
  return `${(rps / 1000).toFixed(1)}k rows/s`;
};

const getDefaultSinceDate = () => {
  const yesterday = new Date();
  yesterday.setDate(yesterday.getDate() - 1);
  return yesterday.toISOString().slice(0, 16); // Format for datetime-local input
};

export default function RunPanel({
  entities,
  selectedEntities,
  onEntitiesChange,
  isRunning,
  currentProgress,
  onRun,
  onStop,
}: RunPanelProps) {
  const [mode, setMode] = useState<"delta" | "backfill">("delta");
  const [dryRun, setDryRun] = useState(false);
  const [sinceDate, setSinceDate] = useState(getDefaultSinceDate());
  const [running, setRunning] = useState(false);

  const handleToggleEntity = (entity: string) => {
    if (selectedEntities.includes(entity)) {
      onEntitiesChange(selectedEntities.filter(e => e !== entity));
    } else {
      onEntitiesChange([...selectedEntities, entity]);
    }
  };

  const handleRun = async () => {
    if (selectedEntities.length === 0) return;

    setRunning(true);
    try {
      const options: RunOptions = {
        mode,
        dryRun,
        since: mode === "delta" ? new Date(sinceDate).toISOString() : undefined
      };
      await onRun(options);
    } finally {
      setRunning(false);
    }
  };

  const getProgressPercentage = () => {
    if (!currentProgress?.totalPages || !currentProgress?.completedPages) return 0;
    return Math.round((currentProgress.completedPages / currentProgress.totalPages) * 100);
  };

  const handleStop = async () => {
    setRunning(true);
    try {
      await onStop();
    } finally {
      setRunning(false);
    }
  };

  return (
    <div className="ecc-card">
      <div className="ecc-card__header">
        <h3 className="ecc-card__title">Sync Control</h3>
      </div>

      <div className="ecc-card__body space-y-4">
        {/* Entity Selection */}
        <div>
          <label className="ecc-field-label mb-2 block">Entities:</label>
          <div className="space-y-2">
            {entities.map(entity => (
              <label key={entity} className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  checked={selectedEntities.includes(entity)}
                  onChange={() => handleToggleEntity(entity)}
                  className="ecc-checkbox"
                  data-testid={`checkbox-entity-${entity}`}
                />
                <span className="ecc-field-value capitalize">{entity}</span>
              </label>
            ))}
          </div>
        </div>

        {/* Mode Selection */}
        <div>
          <label className="ecc-field-label mb-2 block">Mode:</label>
          <div className="space-y-2">
            <label className="flex items-center space-x-2">
              <input
                type="radio"
                name="mode"
                value="delta"
                checked={mode === "delta"}
                onChange={(e) => setMode(e.target.value as "delta")}
                className="ecc-radio"
                data-testid="radio-mode-delta"
              />
              <span className="ecc-field-value">Delta (Recent changes)</span>
            </label>
            <label className="flex items-center space-x-2">
              <input
                type="radio"
                name="mode"
                value="backfill"
                checked={mode === "backfill"}
                onChange={(e) => setMode(e.target.value as "backfill")}
                className="ecc-radio"
                data-testid="radio-mode-backfill"
              />
              <span className="ecc-field-value">Backfill (All data)</span>
            </label>
          </div>
        </div>

        {/* Since Date Picker for Delta Mode */}
        {mode === "delta" && (
          <div>
            <label className="ecc-field-label mb-2 block flex items-center space-x-1">
              <Calendar className="w-4 h-4" />
              <span>Since Date (UTC):</span>
            </label>
            <input
              type="datetime-local"
              value={sinceDate}
              onChange={(e) => setSinceDate(e.target.value)}
              className="ecc-input w-full"
              data-testid="input-since-date"
            />
            <p className="text-xs text-gray-500 mt-1">
              Only sync records modified after this date/time
            </p>
          </div>
        )}

        {/* Dry Run Mode Toggle */}
        <div>
          <label className="flex items-center space-x-2">
            <input
              type="checkbox"
              checked={dryRun}
              onChange={(e) => setDryRun(e.target.checked)}
              className="ecc-checkbox"
              data-testid="checkbox-dry-run"
            />
            <span className="ecc-field-label flex items-center space-x-1">
              <TestTube className="w-4 h-4" />
              <span>Dry Run (Preview only, no changes)</span>
            </span>
          </label>
          {dryRun && (
            <p className="text-xs text-blue-600 dark:text-blue-400 mt-1">
              â„¹ï¸ This will simulate the sync without making any actual changes
            </p>
          )}
        </div>

        {/* Enhanced Progress Display */}
        {(isRunning || currentProgress) && (
          <div className="ecc-card p-4 bg-blue-50 dark:bg-blue-950">
            <div className="space-y-3">
              {/* Status and Entity */}
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-2">
                  <Activity className="w-4 h-4" />
                  <span className="ecc-field-label">Status:</span>
                  <span className={`ecc-badge ${isRunning ? 'ecc-badge--info' : 'ecc-badge--secondary'}`}>
                    {isRunning ? (dryRun ? 'Dry Run' : 'Running') : 'Idle'}
                  </span>
                </div>

                {currentProgress && (
                  <div className="text-sm text-gray-600 dark:text-gray-400">
                    <span className="capitalize font-medium">{currentProgress.entity}</span>
                  </div>
                )}
              </div>

              {/* Progress Bar */}
              {currentProgress?.totalPages && (
                <div>
                  <div className="flex items-center justify-between text-sm mb-1">
                    <span className="ecc-field-label">Progress:</span>
                    <span className="font-mono text-xs">
                      {currentProgress.completedPages || 0}/{currentProgress.totalPages} pages
                      ({getProgressPercentage()}%)
                    </span>
                  </div>
                  <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2">
                    <div 
                      className="bg-blue-500 h-2 rounded-full transition-all duration-300"
                      style={{ width: `${getProgressPercentage()}%` }}
                    ></div>
                  </div>
                </div>
              )}

              {/* Detailed Progress Info */}
              {currentProgress && (
                <div className="grid grid-cols-2 gap-4 text-sm">
                  <div className="ecc-field-row">
                    <span className="ecc-field-label">Current Page:</span>
                    <span className="ecc-field-value font-mono">{currentProgress.page.toLocaleString()}</span>
                  </div>
                  <div className="ecc-field-row">
                    <span className="ecc-field-label">Rows Processed:</span>
                    <span className="ecc-field-value font-mono">{currentProgress.rows.toLocaleString()}</span>
                  </div>

                  {/* RPS Display */}
                  {currentProgress.rps && (
                    <div className="ecc-field-row">
                      <span className="ecc-field-label flex items-center space-x-1">
                        <BarChart3 className="w-3 h-3" />
                        <span>Rate:</span>
                      </span>
                      <span className="ecc-field-value font-mono text-green-600 dark:text-green-400">
                        {formatRPS(currentProgress.rps)}
                      </span>
                    </div>
                  )}

                  {/* ETA Display */}
                  {currentProgress.eta && (
                    <div className="ecc-field-row">
                      <span className="ecc-field-label flex items-center space-x-1">
                        <Clock className="w-3 h-3" />
                        <span>ETA:</span>
                      </span>
                      <span className="ecc-field-value font-mono text-blue-600 dark:text-blue-400">
                        {formatETA(currentProgress.eta)}
                      </span>
                    </div>
                  )}

                  {/* Runtime Display */}
                  {currentProgress.startTime && (
                    <div className="ecc-field-row col-span-2">
                      <span className="ecc-field-label">Runtime:</span>
                      <span className="ecc-field-value font-mono">
                        {formatETA(Date.now() - currentProgress.startTime)}
                      </span>
                    </div>
                  )}
                </div>
              )}
            </div>
          </div>
        )}
      </div>

      <div className="ecc-card__footer">
        <div className="flex space-x-2">
          <button
            className="ecc-button ecc-button--primary"
            onClick={handleRun}
            disabled={running || selectedEntities.length === 0}
            data-testid="button-run-sync"
          >
            {running ? (
              <>
                <RefreshCw className="w-4 h-4 animate-spin" />
                Running...
              </>
            ) : (
              <>
                {mode === "delta" ? <Play className="w-4 h-4" /> : <Database className="w-4 h-4" />}
                Run {mode === "delta" ? "Now" : "Backfill"}
              </>
            )}
          </button>

          {isRunning && (
            <button
              className="ecc-button ecc-button--error"
              onClick={handleStop}
              disabled={running}
              data-testid="button-stop-sync"
            >
              <Square className="w-4 h-4" />
              Stop
            </button>
          )}
        </div>
      </div>
    </div>
  );
}

import { CheckCircle, AlertTriangle, XCircle, RefreshCw, Shield, Timer, TrendingUp, RotateCcw } from "lucide-react";
import { useState, useEffect } from "react";

interface PingResponse {
  ok: boolean;
  baseUrl?: string;
  authenticated: boolean;
  sampleCount?: number;
  elapsedMs: number;
  error?: string;
  healthScore?: number;
  rateLimit?: {
    limit: number;
    remaining: number;
    resetTime: number;
  };
  circuitBreaker?: {
    isOpen: boolean;
    failures: number;
    cooldownUntil?: number;
  };
  fullResponse?: any;
}

interface CircuitBreakerStatus {
  isOpen: boolean;
  failures: number;
  cooldownUntil?: number;
}

interface RateLimitStatus {
  limit: number;
  remaining: number;
  resetTime: number;
}

interface StatusCardProps {
  title: string;
  status: "healthy" | "degraded" | "failing";
  baseUrl?: string;
  authenticated?: boolean;
  healthScore?: number;
  rateLimit?: RateLimitStatus;
  circuitBreaker?: CircuitBreakerStatus;
  lastSuccess?: string;
  lastError?: string;
  onPing: () => Promise<PingResponse>;
  onResetCircuitBreaker?: () => Promise<void>;
}

const StatusChip = ({ status }: { status: "healthy" | "degraded" | "failing" }) => {
  const config = {
    healthy: { icon: CheckCircle, label: "âœ… Healthy", className: "ecc-badge--success" },
    degraded: { icon: AlertTriangle, label: "âš ï¸ Degraded", className: "ecc-badge--warning" },
    failing: { icon: XCircle, label: "âŒ Failing", className: "ecc-badge--error" }
  };

  const { icon: Icon, label, className } = config[status];
  return (
    <div className={`ecc-badge ${className}`}>
      <Icon className="w-4 h-4" />
      {label}
    </div>
  );
};

// Utility functions
const formatTimeUntil = (timestamp: number) => {
  const now = Date.now();
  const diff = timestamp - now;
  if (diff <= 0) return "Now";

  const minutes = Math.floor(diff / 60000);
  const seconds = Math.floor((diff % 60000) / 1000);

  if (minutes > 0) {
    return `${minutes}m ${seconds}s`;
  }
  return `${seconds}s`;
};

const getHealthScoreColor = (score: number) => {
  if (score >= 90) return "text-green-500";
  if (score >= 70) return "text-yellow-500";
  return "text-red-500";
};

const getHealthScoreLabel = (score: number) => {
  if (score >= 95) return "Excellent";
  if (score >= 85) return "Good";
  if (score >= 70) return "Fair";
  if (score >= 50) return "Poor";
  return "Critical";
};

export default function StatusCard({ 
  title, 
  status, 
  baseUrl, 
  authenticated, 
  healthScore,
  rateLimit, 
  circuitBreaker,
  lastSuccess, 
  lastError,
  onPing,
  onResetCircuitBreaker
}: StatusCardProps) {
  const [pinging, setPinging] = useState(false);
  const [resettingCircuitBreaker, setResettingCircuitBreaker] = useState(false);
  const [pingResult, setPingResult] = useState<PingResponse | null>(null);
  const [currentTime, setCurrentTime] = useState(Date.now());

  // Update current time every second for countdown displays
  useEffect(() => {
    const interval = setInterval(() => setCurrentTime(Date.now()), 1000);
    return () => clearInterval(interval);
  }, []);

  const handlePing = async () => {
    setPinging(true);
    try {
      const result = await onPing();
      setPingResult(result);
    } catch (err) {
      setPingResult({
        ok: false,
        authenticated: false,
        elapsedMs: 0,
        error: String(err)
      });
    } finally {
      setPinging(false);
    }
  };

  const handleResetCircuitBreaker = async () => {
    if (!onResetCircuitBreaker) return;

    setResettingCircuitBreaker(true);
    try {
      await onResetCircuitBreaker();
    } catch (err) {
      console.error('Failed to reset circuit breaker:', err);
    } finally {
      setResettingCircuitBreaker(false);
    }
  };

  return (
    <div className="ecc-card">
      <div className="ecc-card__header">
        <h3 className="ecc-card__title">{title}</h3>
        <StatusChip status={status} />
      </div>

      <div className="ecc-card__body">
        <div className="space-y-3">
          <div className="ecc-field-row">
            <span className="ecc-field-label">Base URL:</span>
            <span className="ecc-field-value font-mono text-sm">{baseUrl || 'Not configured'}</span>
          </div>

          <div className="ecc-field-row">
            <span className="ecc-field-label">Auth OK:</span>
            <span className={`ecc-badge ${authenticated ? 'ecc-badge--success' : 'ecc-badge--error'}`}>
              {authenticated ? 'Yes' : 'No'}
            </span>
          </div>

          {/* Health Score Display */}
          {healthScore !== undefined && (
            <div className="ecc-field-row">
              <span className="ecc-field-label flex items-center space-x-1">
                <TrendingUp className="w-4 h-4" />
                <span>Health Score:</span>
              </span>
              <span className={`ecc-field-value font-mono ${getHealthScoreColor(healthScore)}`}>
                {healthScore.toFixed(1)}% ({getHealthScoreLabel(healthScore)})
              </span>
            </div>
          )}

          {/* Circuit Breaker Status */}
          {circuitBreaker && (
            <div className="ecc-field-row">
              <span className="ecc-field-label flex items-center space-x-1">
                <Shield className="w-4 h-4" />
                <span>Circuit Breaker:</span>
              </span>
              <div className="flex items-center space-x-2">
                <span className={`ecc-badge ${circuitBreaker.isOpen ? 'ecc-badge--error' : 'ecc-badge--success'}`}>
                  {circuitBreaker.isOpen ? 'Open' : 'Closed'}
                </span>
                {circuitBreaker.failures > 0 && (
                  <span className="text-sm text-gray-500">
                    {circuitBreaker.failures} failures
                  </span>
                )}
                {circuitBreaker.cooldownUntil && circuitBreaker.cooldownUntil > currentTime && (
                  <span className="text-sm text-yellow-600 font-mono">
                    cooldown: {formatTimeUntil(circuitBreaker.cooldownUntil)}
                  </span>
                )}
              </div>
            </div>
          )}

          {/* Rate Limit Status with Countdown */}
          {rateLimit && (
            <div className="ecc-field-row">
              <span className="ecc-field-label flex items-center space-x-1">
                <Timer className="w-4 h-4" />
                <span>Rate Limit:</span>
              </span>
              <div className="flex items-center space-x-2">
                <span className="ecc-field-value font-mono text-sm">
                  {rateLimit.remaining}/{rateLimit.limit}
                </span>
                {rateLimit.resetTime > currentTime && (
                  <span className="text-sm text-gray-500 font-mono">
                    reset: {formatTimeUntil(rateLimit.resetTime)}
                  </span>
                )}
              </div>
            </div>
          )}

          {lastSuccess && (
            <div className="ecc-field-row">
              <span className="ecc-field-label">Last Success:</span>
              <span className="ecc-field-value text-sm">{lastSuccess}</span>
            </div>
          )}

          {lastError && (
            <div className="ecc-field-row">
              <span className="ecc-field-label">Last Error:</span>
              <span className="ecc-field-value text-sm text-red-400">{lastError}</span>
            </div>
          )}

          {/* Enhanced Ping Results */}
          {pingResult && (
            <div className="space-y-2">
              <div className="ecc-field-row">
                <span className="ecc-field-label">Ping Result:</span>
                <span className={`ecc-badge ${pingResult.ok ? 'ecc-badge--success' : 'ecc-badge--error'}`}>
                  {pingResult.ok ? `âœ“ ${pingResult.elapsedMs}ms` : 'âœ— Failed'}
                </span>
              </div>

              {pingResult.sampleCount && (
                <div className="ecc-field-row">
                  <span className="ecc-field-label">Sample Count:</span>
                  <span className="ecc-field-value font-mono text-sm">{pingResult.sampleCount.toLocaleString()}</span>
                </div>
              )}

              {pingResult.healthScore !== undefined && (
                <div className="ecc-field-row">
                  <span className="ecc-field-label">Ping Health Score:</span>
                  <span className={`ecc-field-value font-mono ${getHealthScoreColor(pingResult.healthScore)}`}>
                    {pingResult.healthScore.toFixed(1)}%
                  </span>
                </div>
              )}

              {pingResult.rateLimit && (
                <div className="ecc-field-row">
                  <span className="ecc-field-label">Rate Limit Response:</span>
                  <span className="ecc-field-value font-mono text-sm">
                    {pingResult.rateLimit.remaining}/{pingResult.rateLimit.limit}
                  </span>
                </div>
              )}

              {pingResult.error && (
                <div className="ecc-field-row">
                  <span className="ecc-field-label">Ping Error:</span>
                  <span className="ecc-field-value text-sm text-red-400">{pingResult.error}</span>
                </div>
              )}
            </div>
          )}
        </div>
      </div>

      <div className="ecc-card__footer">
        <div className="flex space-x-2">
          <button 
            className="ecc-button ecc-button--secondary"
            onClick={handlePing}
            disabled={pinging}
            data-testid="button-ping"
          >
            {pinging ? (
              <>
                <RefreshCw className="w-4 h-4 animate-spin" />
                Pinging...
              </>
            ) : (
              'Ping'
            )}
          </button>

          {/* Circuit Breaker Reset Button */}
          {circuitBreaker?.isOpen && onResetCircuitBreaker && (
            <button
              className="ecc-button ecc-button--warning"
              onClick={handleResetCircuitBreaker}
              disabled={resettingCircuitBreaker}
              data-testid="button-reset-circuit-breaker"
            >
              {resettingCircuitBreaker ? (
                <>
                  <RefreshCw className="w-4 h-4 animate-spin" />
                  Resetting...
                </>
              ) : (
                <>
                  <RotateCcw className="w-4 h-4" />
                  Reset Circuit Breaker
                </>
              )}
            </button>
          )}
        </div>
      </div>
    </div>
  );
}

server/routes/admin/sync.ts.content:
// server/routes/admin/sync.ts
import { Router, Request, Response } from "express";
import { sbAdmin } from "../../lib/supabaseAdmin";
import { dlPaginate, dlFetchFull } from "../../clients/doorloop";

export const sync = Router();

function isAuthorized(req: Request) {
  const token = req.headers['x-admin-token'] || 
                req.headers['authorization']?.replace('Bearer ', '') ||
                req.query.token; // Support query parameter for EventSource compatibility
  return token && token === process.env.ADMIN_SYNC_TOKEN;
}

// In-memory state for sync orchestration
let syncState = {
  isRunning: false,
  currentRun: null as any,
  lock: null as { runId: string; startedAt: number; entity?: string } | null,
  progress: {
    entity: '',
    page: 0,
    rows: 0,
    totalPages: 0,
    startedAt: 0,
    eta: 0,
    rps: 0,
  },
  lastRun: {
    timestamp: 0,
    success: false,
    error: null as string | null,
  },
};

// SSE clients for live log streaming
const sseClients = new Set<Response>();

// Dead Letter Queue (in-memory for now, could be moved to DB)
const dlq: Array<{
  id: string;
  entity: string;
  error: string;
  payload: any;
  timestamp: number;
  retryCount: number;
}> = [];

// Teams webhook helper
async function sendTeamsAlert(text: string) {
  const webhookUrl = process.env.INTEGRATIONS_TEAMS_WEBHOOK;
  if (!webhookUrl) return;

  try {
    await fetch(webhookUrl, {
      method: 'POST',
      headers: { 'content-type': 'application/json' },
      body: JSON.stringify({ text }),
    });
  } catch (error) {
    console.error('[teams] Alert failed:', error);
  }
}

// Log event emitter for SSE
function emitLogEvent(event: {
  type: 'info' | 'warn' | 'error' | 'progress';
  entity?: string;
  page?: number;
  rows?: number;
  level?: string;
  message: string;
  runId?: string;
  timestamp?: number;
}) {
  const logEvent = {
    ...event,
    timestamp: event.timestamp || Date.now(),
    runId: event.runId || syncState.currentRun?.id,
  };

  // Send to all SSE clients
  sseClients.forEach(client => {
    try {
      client.write(`event: log\ndata: ${JSON.stringify(logEvent)}\n\n`);
    } catch (error) {
      // Client disconnected, remove from set
      sseClients.delete(client);
    }
  });

  console.log(`[sync] ${logEvent.type.toUpperCase()}: ${logEvent.message}`);
}

// Get current sync status
sync.get("/status", async (req, res) => {
  if (!isAuthorized(req)) {
    return res.status(401).json({ error: 'unauthorized' });
  }

  // Get scheduler configuration
  const autoSyncEnabled = process.env.AUTO_SYNC_ENABLED === 'true';
  const intervalMinutes = parseInt(process.env.AUTO_SYNC_INTERVAL_MINUTES || '10');
  const fullSyncHour = parseInt(process.env.AUTO_SYNC_FULL_AT_HOUR_UTC || '5');
  const entities = (process.env.SYNC_ENTITIES || 'owners,properties,units,leases,tenants').split(',');

  // Calculate next run time
  const now = new Date();
  const nextRun = new Date(now.getTime() + intervalMinutes * 60 * 1000);
  const nextFullSync = new Date();
  nextFullSync.setUTCHours(fullSyncHour, 0, 0, 0);
  if (nextFullSync <= now) {
    nextFullSync.setUTCDate(nextFullSync.getUTCDate() + 1);
  }

  res.json({
    ok: true,
    scheduler: {
      enabled: autoSyncEnabled,
      intervalMinutes,
      entities,
      fullSyncHourUTC: fullSyncHour,
      nextRun: nextRun.toISOString(),
      nextFullSync: nextFullSync.toISOString(),
    },
    currentRun: syncState.isRunning ? {
      ...syncState.currentRun,
      progress: syncState.progress,
    } : null,
    lock: syncState.lock,
    lastRun: syncState.lastRun,
    dlqCount: dlq.length,
  });
});

// Start manual sync run
sync.post("/run", async (req, res) => {
  if (!isAuthorized(req)) {
    return res.status(401).json({ error: 'unauthorized' });
  }

  if (syncState.isRunning) {
    return res.status(409).json({ error: 'sync already running' });
  }

  const { entities = ['owners'], mode = 'delta', since, dryRun = false } = req.body;

  if (!Array.isArray(entities) || entities.length === 0) {
    return res.status(400).json({ error: 'entities array is required' });
  }

  const runId = `manual_${Date.now()}`;

  // Set up sync state
  syncState.isRunning = true;
  syncState.currentRun = {
    id: runId,
    entities,
    mode,
    since,
    dryRun,
    startedAt: Date.now(),
  };
  syncState.lock = {
    runId,
    startedAt: Date.now(),
  };
  syncState.progress = {
    entity: '',
    page: 0,
    rows: 0,
    totalPages: 0,
    startedAt: Date.now(),
    eta: 0,
    rps: 0,
  };

  // Log sync start
  await logAdminAction(req, 'SYNC_START', { runId, entities, mode, dryRun });
  emitLogEvent({
    type: 'info',
    message: `Starting ${mode} sync for entities: ${entities.join(', ')}${dryRun ? ' (DRY RUN)' : ''}`,
    runId,
  });

  // Start sync in background
  runSyncInBackground(runId, entities, mode, dryRun, since);

  // Send Teams alert for sync start
  await sendTeamsAlert(`ðŸš€ DoorLoop ${mode} sync started for ${entities.join(', ')}${dryRun ? ' (DRY RUN)' : ''}`);

  res.json({ 
    ok: true, 
    runId,
    message: `${dryRun ? 'Dry run' : 'Sync'} started for ${entities.length} entities`,
  });
});

// Stop current sync
sync.post("/stop", async (req, res) => {
  if (!isAuthorized(req)) {
    return res.status(401).json({ error: 'unauthorized' });
  }

  if (!syncState.isRunning) {
    return res.status(400).json({ error: 'no sync running' });
  }

  const runId = syncState.currentRun?.id;

  // Reset sync state
  syncState.isRunning = false;
  syncState.currentRun = null;
  syncState.lock = null;

  await logAdminAction(req, 'SYNC_STOP', { runId });
  emitLogEvent({
    type: 'warn',
    message: `Sync stopped by admin`,
    runId,
  });

  res.json({ ok: true, message: 'Sync stopped' });
});

// SSE log streaming
sync.get("/logs/stream", (req, res) => {
  if (!isAuthorized(req)) {
    return res.status(401).json({ error: 'unauthorized' });
  }

  // Set up SSE headers
  res.setHeader('Content-Type', 'text/event-stream');
  res.setHeader('Cache-Control', 'no-cache');
  res.setHeader('Connection', 'keep-alive');
  res.setHeader('Access-Control-Allow-Origin', '*');
  res.setHeader('Access-Control-Allow-Headers', 'Cache-Control');

  // Add client to set
  sseClients.add(res);

  // Send initial event
  res.write(`event: connected\ndata: ${JSON.stringify({ message: 'Connected to sync logs' })}\n\n`);

  // Handle client disconnect
  req.on('close', () => {
    sseClients.delete(res);
  });

  req.on('end', () => {
    sseClients.delete(res);
  });
});

// Get sync run history
sync.get("/runs", async (req, res) => {
  if (!isAuthorized(req)) {
    return res.status(401).json({ error: 'unauthorized' });
  }

  const limit = Math.min(50, Number(req.query.limit) || 20);

  try {
    const { data: auditEvents } = await sbAdmin
      .from('audit_events')
      .select('*')
      .in('event_type', ['AUTO_SYNC', 'MANUAL_SYNC', 'SYNC_START', 'SYNC_STOP'])
      .order('created_at', { ascending: false })
      .limit(limit);

    if (!auditEvents) {
      return res.json({ ok: true, runs: [] });
    }

    const runs = auditEvents.map(event => ({
      id: event.id?.toString() || Math.random().toString(),
      timestamp: event.created_at,
      entity: event.payload?.entity || event.payload?.entities?.join(',') || 'unknown',
      mode: event.payload?.mode === 'backfill' ? 'backfill' as const : 'delta' as const,
      rows: event.payload?.rows || 0,
      duration: event.payload?.duration || 0,
      result: event.payload?.error ? 'error' as const : 'success' as const,
      error: event.payload?.error || undefined,
      fullMessage: event.payload?.fullError || undefined,
      fullDetails: event.payload || undefined,
      dryRun: event.payload?.dryRun || false,
    }));

    res.json({ ok: true, runs });
  } catch (error) {
    console.error('[sync] Error fetching runs:', error);
    res.status(500).json({ ok: false, error: 'Failed to fetch runs' });
  }
});

// Get DLQ items
sync.get("/dlq", async (req, res) => {
  if (!isAuthorized(req)) {
    return res.status(401).json({ error: 'unauthorized' });
  }

  const entity = req.query.entity as string;
  const filteredDlq = entity ? dlq.filter(item => item.entity === entity) : dlq;

  res.json({
    ok: true,
    items: filteredDlq.slice(0, 50), // Limit to 50 items
    total: filteredDlq.length,
  });
});

// Requeue DLQ item
sync.post("/dlq/requeue", async (req, res) => {
  if (!isAuthorized(req)) {
    return res.status(401).json({ error: 'unauthorized' });
  }

  const { id } = req.body;
  const itemIndex = dlq.findIndex(item => item.id === id);

  if (itemIndex === -1) {
    return res.status(404).json({ error: 'DLQ item not found' });
  }

  const item = dlq[itemIndex];

  // Remove from DLQ and attempt reprocess
  dlq.splice(itemIndex, 1);

  emitLogEvent({
    type: 'info',
    message: `Requeuing ${item.entity} item ${item.id}`,
  });

  // Attempt to reprocess the item with retry logic
  const maxRetries = 3;
  let lastError: Error | null = null;

  for (let attempt = 1; attempt <= maxRetries; attempt++) {
    try {
      // Implement actual reprocessing logic for the specific entity
      await reprocessSingleItem(item.entity, item.payload);

      emitLogEvent({
        type: 'info',
        message: `Successfully requeued ${item.entity} item ${item.id} on attempt ${attempt}`,
      });

      // Success - break out of retry loop
      lastError = null;
      break;

    } catch (error) {
      lastError = error instanceof Error ? error : new Error(String(error));

      emitLogEvent({
        type: 'warn',
        message: `Requeue attempt ${attempt}/${maxRetries} failed for ${item.entity} item ${item.id}: ${lastError.message}`,
      });

      // Wait before retrying (exponential backoff)
      if (attempt < maxRetries) {
        const delay = Math.pow(2, attempt) * 1000; // 2s, 4s, 8s
        await new Promise(resolve => setTimeout(resolve, delay));
      }
    }
  }

  // If all retries failed, put it back in DLQ
  if (lastError) {
    dlq.push({ 
      ...item, 
      retryCount: item.retryCount + 1, 
      timestamp: Date.now(),
      error: lastError.message 
    });

    emitLogEvent({
      type: 'error',
      message: `Failed to requeue ${item.entity} item ${item.id} after ${maxRetries} attempts: ${lastError.message}`,
    });

    return res.status(500).json({ ok: false, error: 'Requeue failed after retries' });
  }

  await logAdminAction(req, 'DLQ_REQUEUE', { id, entity: item.entity });

  res.json({ ok: true, message: 'Item requeued successfully' });
});

// Background sync execution
async function runSyncInBackground(
  runId: string,
  entities: string[],
  mode: 'delta' | 'backfill',
  dryRun: boolean,
  since?: string
) {
  const startTime = Date.now();
  let totalRows = 0;
  let hasError = false;
  let errorMessage = '';

  try {
    for (const entity of entities) {
      if (!syncState.isRunning) break; // Check if stopped

      syncState.progress.entity = entity;
      syncState.progress.page = 0;
      syncState.progress.rows = 0;
      syncState.progress.totalPages = 0; // Will be estimated during pagination

      emitLogEvent({
        type: 'info',
        message: `Starting ${entity} sync`,
        entity,
        runId,
      });

      try {
        // Simulate sync for the entity
        const entityData = await dlPaginate(
          `/${entity}`,
          since ? { updated_after: since } : {},
          200,
          (page, items, rateLimit) => {
            syncState.progress.page = page;
            syncState.progress.rows += items;
            totalRows += items;

            // Calculate ETA and RPS
            const elapsed = Date.now() - startTime;
            const rps = totalRows / (elapsed / 1000);
            syncState.progress.rps = Math.round(rps * 100) / 100;

            // Dynamic ETA estimation based on page rate and batch size
            // Estimate remaining pages based on the pattern: if we got full pages, there's likely more
            if (items === 200 && page <= 3) {
              // Early stage: estimate conservatively based on typical entity sizes
              syncState.progress.totalPages = Math.max(page * 2, 10);
            } else if (items < 200) {
              // Last page detected (partial batch)
              syncState.progress.totalPages = page;
            } else if (page > 3) {
              // Mid-stage: extrapolate based on current rate
              syncState.progress.totalPages = Math.max(syncState.progress.totalPages, page + 5);
            }

            const remainingPages = Math.max(0, syncState.progress.totalPages - page);
            const avgTimePerPage = elapsed / page;
            syncState.progress.eta = remainingPages > 0 ? Math.round(remainingPages * avgTimePerPage) : 0;

            emitLogEvent({
              type: 'progress',
              entity,
              page,
              rows: items,
              message: `Page ${page}: ${items} items${rateLimit ? ` (rate limit: ${rateLimit.remaining}/${rateLimit.limit})` : ''} | RPS: ${syncState.progress.rps} | ETA: ${Math.round(syncState.progress.eta/1000)}s`,
              runId,
            });
          }
        );

        if (!dryRun) {
          // TODO: Implement actual database writes here
          emitLogEvent({
            type: 'info',
            message: `Would insert ${entityData.length} ${entity} records`,
            entity,
            runId,
          });
        } else {
          emitLogEvent({
            type: 'info',
            message: `DRY RUN: Found ${entityData.length} ${entity} records`,
            entity,
            runId,
          });
        }

      } catch (error) {
        const errorMsg = error instanceof Error ? error.message : String(error);
        errorMessage = errorMsg;
        hasError = true;

        // Add to DLQ
        dlq.push({
          id: `${entity}_${Date.now()}`,
          entity,
          error: errorMsg,
          payload: { runId, mode, since },
          timestamp: Date.now(),
          retryCount: 0,
        });

        emitLogEvent({
          type: 'error',
          entity,
          message: `Error syncing ${entity}: ${errorMsg}`,
          runId,
        });

        // Send Teams alert for errors
        await sendTeamsAlert(`ðŸš¨ DoorLoop sync failed for ${entity}: ${errorMsg.slice(0, 200)}`);
      }
    }

    // Complete sync
    const duration = Date.now() - startTime;

    syncState.isRunning = false;
    syncState.currentRun = null;
    syncState.lock = null;
    syncState.lastRun = {
      timestamp: Date.now(),
      success: !hasError,
      error: hasError ? errorMessage : null,
    };

    // Log completion
    const eventType = hasError ? 'SYNC_ERROR' : 'SYNC_COMPLETE';
    await sbAdmin.from('audit_events').insert({
      event_type: eventType,
      payload: {
        runId,
        entities,
        mode,
        rows: totalRows,
        duration,
        dryRun,
        error: hasError ? errorMessage : null,
      },
    });

    emitLogEvent({
      type: hasError ? 'error' : 'info',
      message: `Sync completed in ${duration}ms. ${totalRows} total rows processed${hasError ? ` with errors` : ''}`,
      runId,
    });

    // Send Teams completion alert
    const status = hasError ? 'âŒ Failed' : 'âœ… Completed';
    await sendTeamsAlert(`${status} DoorLoop sync: ${totalRows} rows in ${Math.round(duration/1000)}s`);

  } catch (error) {
    // Fatal error
    const errorMsg = error instanceof Error ? error.message : String(error);

    syncState.isRunning = false;
    syncState.currentRun = null;
    syncState.lock = null;
    syncState.lastRun = {
      timestamp: Date.now(),
      success: false,
      error: errorMsg,
    };

    emitLogEvent({
      type: 'error',
      message: `Fatal sync error: ${errorMsg}`,
      runId,
    });

    await sendTeamsAlert(`ðŸš¨ DoorLoop sync fatal error: ${errorMsg.slice(0, 200)}`);
  }
}

// Helper to reprocess a single DLQ item
async function reprocessSingleItem(entity: string, payload: any) {
  const { runId, mode, since } = payload;

  // For now, simulate reprocessing by fetching a small sample
  // In a real implementation, this would process the specific failed item
  try {
    const response = await dlFetchFull(`/${entity}`, {
      method: "GET",
      query: { page_size: 1 }
    });

    if (!response.data) {
      throw new Error(`No data returned for ${entity}`);
    }

    // Simulate processing the item (in real implementation, would insert/update to DB)
    console.log(`[dlq] Reprocessed ${entity} item successfully`);

    return true;
  } catch (error) {
    console.error(`[dlq] Reprocessing failed for ${entity}:`, error);
    throw error;
  }
}

// Helper to log admin actions
async function logAdminAction(req: Request, action: string, payload: any) {
  try {
    await sbAdmin.from('audit_events').insert({
      event_type: action,
      payload: {
        ...payload,
        admin_ip: req.ip,
        user_agent: req.get('user-agent'),
      },
    });
  } catch (error) {
    console.error('[sync] Failed to log admin action:', error);
  }
}

src/features/owners/api/ownerTransfer.ts.content:
import { api } from "../../../lib/api";

const BASE = import.meta.env.VITE_API_BASE_URL || "";
// ADMIN_TOKEN removed for security - server handles authentication

export interface Owner {
  id: string;
  name: string;
  email?: string;
  phone?: string;
}

export interface CreateOwnerData {
  name: string;
  email?: string;
  phone?: string;
}

async function req(path: string, body?: any, method: "POST"|"GET" = "POST") {
  const url = `${BASE}/api/owner-transfer${path}`;
  const res = await fetch(url, {
    method,
    headers: {
      "Content-Type": "application/json",
      // x-admin-token removed - server handles authentication
    },
    body: method === "POST" ? JSON.stringify(body || {}) : undefined,
  });
  if (!res.ok) throw new Error(`${method} ${path} failed: ${res.status}`);
  return res.json();
}

/**
 * Search for owners by name
 */
export async function searchOwners(query: string): Promise<Owner[]> {
  const response = await api(`/api/owners/search?q=${encodeURIComponent(query)}`, {
    method: "GET",
  });

  return response as Owner[];
}

/**
 * Create a new owner
 */
export async function createOwner(data: CreateOwnerData): Promise<Owner> {
  const response = await api("/api/owners", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(data),
  });

  return response as Owner;
}

export const OwnerTransferAPI = {
  initiate: (payload: {
    sourceOwnerId: string;
    targetOwnerId: string;
    effectiveDate: string; // ISO
    include: {
      properties: boolean; units: boolean; leases: boolean; tenants: boolean; files: boolean; workOrders: boolean; comms: boolean; financials: boolean;
    };
    notes?: string;
  }) => req("/initiate", payload),
  approve:   (id: string) => req(`/approve?id=${encodeURIComponent(id)}`),
  authorize: (id: string) => req(`/authorize?id=${encodeURIComponent(id)}`),
  execute:   (id: string) => req(`/execute?id=${encodeURIComponent(id)}`),
  status:    (id: string) => req(`/status?id=${encodeURIComponent(id)}`, undefined, "GET"),
};

