import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { RotateCcw, Trash2, AlertTriangle, Filter, RefreshCw, Clock, Hash, FileX } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface DLQItem {
  id: string;
  entity: string;
  data: any;
  error: string;
  timestamp: string;
  retryCount: number;
  maxRetries: number;
  runId?: string;
  originalOperation: string;
}

interface DLQPanelProps {
  adminToken: string;
}

export default function DLQPanel({ adminToken }: DLQPanelProps) {
  const [selectedEntity, setSelectedEntity] = useState<string>('');
  const [showDetails, setShowDetails] = useState<Set<string>>(new Set());
  const { showToast } = useToast();

  // Fetch DLQ items
  const { data: dlqData, isLoading, refetch } = useQuery({
    queryKey: ['/api/admin/sync/dlq'],
    queryFn: async () => {
      if (!adminToken) return { items: [], count: 0 };

      return apiRequest('/api/admin/sync/dlq', {
        headers: {
          'X-Admin-Token': adminToken,
        },
      });
    },
    enabled: !!adminToken,
    refetchInterval: 30000, // Refresh every 30 seconds
  });

  // Requeue single item mutation
  const requeueMutation = useMutation({
    mutationFn: async (itemId: string) => {
      return apiRequest(`/api/admin/sync/dlq/${itemId}/requeue`, {
        method: 'POST',
        headers: {
          'X-Admin-Token': adminToken,
        },
      });
    },
    onSuccess: (_, itemId) => {
      showToast({
        title: "Item requeued",
        description: "The failed item has been requeued for processing",
        variant: "success",
      });
      refetch();
    },
    onError: (error: Error) => {
      showToast({
        title: "Requeue failed",
        description: error.message,
        variant: "error",
      });
    },
  });

  // Delete single item mutation
  const deleteMutation = useMutation({
    mutationFn: async (itemId: string) => {
      return apiRequest(`/api/admin/sync/dlq/${itemId}`, {
        method: 'DELETE',
        headers: {
          'X-Admin-Token': adminToken,
        },
      });
    },
    onSuccess: (_, itemId) => {
      showToast({
        title: "Item deleted",
        description: "The failed item has been removed from the queue",
        variant: "success",
      });
      refetch();
    },
    onError: (error: Error) => {
      showToast({
        title: "Delete failed",
        description: error.message,
        variant: "error",
      });
    },
  });

  // Clear all DLQ items mutation
  const clearAllMutation = useMutation({
    mutationFn: async () => {
      return apiRequest('/api/admin/sync/dlq/clear', {
        method: 'POST',
        headers: {
          'X-Admin-Token': adminToken,
        },
      });
    },
    onSuccess: () => {
      showToast({
        title: "DLQ cleared",
        description: "All failed items have been removed from the queue",
        variant: "success",
      });
      refetch();
    },
    onError: (error: Error) => {
      showToast({
        title: "Clear failed",
        description: error.message,
        variant: "error",
      });
    },
  });

  // Requeue all items mutation
  const requeueAllMutation = useMutation({
    mutationFn: async () => {
      return apiRequest('/api/admin/sync/dlq/requeue-all', {
        method: 'POST',
        headers: {
          'X-Admin-Token': adminToken,
        },
      });
    },
    onSuccess: (data) => {
      showToast({
        title: "All items requeued",
        description: `${data.count} items have been requeued for processing`,
        variant: "success",
      });
      refetch();
    },
    onError: (error: Error) => {
      showToast({
        title: "Requeue all failed",
        description: error.message,
        variant: "error",
      });
    },
  });

  const filteredItems = dlqData?.items?.filter((item: DLQItem) => 
    !selectedEntity || item.entity === selectedEntity
  ) || [];

  const entityCounts = dlqData?.items?.reduce((acc: Record<string, number>, item: DLQItem) => {
    acc[item.entity] = (acc[item.entity] || 0) + 1;
    return acc;
  }, {}) || {};

  const formatTimestamp = (timestamp: string) => {
    return new Date(timestamp).toLocaleString();
  };

  const formatTimeAgo = (timestamp: string) => {
    const diff = Date.now() - new Date(timestamp).getTime();
    const minutes = Math.floor(diff / 60000);
    const hours = Math.floor(minutes / 60);
    const days = Math.floor(hours / 24);

    if (days > 0) return `${days}d ago`;
    if (hours > 0) return `${hours}h ago`;
    if (minutes > 0) return `${minutes}m ago`;
    return 'Just now';
  };

  const toggleDetails = (itemId: string) => {
    const newShowDetails = new Set(showDetails);
    if (newShowDetails.has(itemId)) {
      newShowDetails.delete(itemId);
    } else {
      newShowDetails.add(itemId);
    }
    setShowDetails(newShowDetails);
  };

  const getRetryBadgeColor = (retryCount: number, maxRetries: number) => {
    const ratio = retryCount / maxRetries;
    if (ratio < 0.5) return 'ecc-badge--warning';
    if (ratio < 1) return 'ecc-badge--error';
    return 'ecc-badge--error';
  };

  if (!adminToken) {
    return (
      <div className="ecc-card">
        <div className="ecc-card__body">
          <div className="text-center py-8 text-gray-500">
            <FileX className="w-8 h-8 mx-auto mb-2" />
            <p>Admin token required to view DLQ items</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="ecc-card">
      <div className="ecc-card__header">
        <div className="flex items-center justify-between">
          <h3 className="ecc-card__title flex items-center space-x-2">
            <AlertTriangle className="w-5 h-5" />
            <span>Dead Letter Queue</span>
            {dlqData?.count > 0 && (
              <span className="ecc-badge ecc-badge--error">
                {dlqData.count}
              </span>
            )}
          </h3>

          <div className="flex items-center space-x-2">
            <button
              onClick={() => refetch()}
              disabled={isLoading}
              className="ecc-button ecc-button--ghost p-1"
              data-testid="button-refresh-dlq"
            >
              <RefreshCw className={`w-4 h-4 ${isLoading ? 'animate-spin' : ''}`} />
            </button>

            <div className="flex items-center space-x-1">
              <Filter className="w-4 h-4 text-gray-500" />
              <select
                value={selectedEntity}
                onChange={(e) => setSelectedEntity(e.target.value)}
                className="ecc-input text-sm"
                data-testid="select-dlq-entity-filter"
              >
                <option value="">All Entities ({dlqData?.count || 0})</option>
                {Object.entries(entityCounts).map(([entity, count]) => (
                  <option key={entity} value={entity}>
                    {entity} ({count})
                  </option>
                ))}
              </select>
            </div>
          </div>
        </div>
      </div>

      <div className="ecc-card__body">
        {isLoading ? (
          <div className="text-center py-8">
            <RefreshCw className="w-8 h-8 mx-auto mb-2 animate-spin text-gray-400" />
            <p className="text-gray-500">Loading DLQ items...</p>
          </div>
        ) : filteredItems.length === 0 ? (
          <div className="text-center py-8 text-gray-500">
            {dlqData?.count === 0 ? (
              <>
                <FileX className="w-8 h-8 mx-auto mb-2" />
                <p>No failed items in queue</p>
                <p className="text-sm">All syncs are processing successfully!</p>
              </>
            ) : (
              <>
                <Filter className="w-8 h-8 mx-auto mb-2" />
                <p>No items match the selected filter</p>
              </>
            )}
          </div>
        ) : (
          <div className="space-y-3">
            {filteredItems.map((item: DLQItem) => (
              <div key={item.id} className="border rounded-lg p-3 bg-red-50 dark:bg-red-950 border-red-200 dark:border-red-800">
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <div className="flex items-center space-x-2 mb-2">
                      <span className="ecc-badge ecc-badge--secondary capitalize">
                        {item.entity}
                      </span>
                      <span className={`ecc-badge ${getRetryBadgeColor(item.retryCount, item.maxRetries)}`}>
                        Retry {item.retryCount}/{item.maxRetries}
                      </span>
                      {item.runId && (
                        <span className="text-xs text-gray-500 font-mono">
                          Run: {item.runId.slice(0, 8)}
                        </span>
                      )}
                    </div>

                    <div className="space-y-1 text-sm">
                      <div className="ecc-field-row">
                        <span className="ecc-field-label">Operation:</span>
                        <span className="ecc-field-value font-mono">{item.originalOperation}</span>
                      </div>
                      <div className="ecc-field-row">
                        <span className="ecc-field-label">Error:</span>
                        <span className="ecc-field-value text-red-600 dark:text-red-400">
                          {item.error}
                        </span>
                      </div>
                      <div className="ecc-field-row">
                        <span className="ecc-field-label">Time:</span>
                        <span className="ecc-field-value">
                          {formatTimestamp(item.timestamp)} ({formatTimeAgo(item.timestamp)})
                        </span>
                      </div>
                    </div>

                    {/* Expandable Details */}
                    {item.data && (
                      <div className="mt-2">
                        <button
                          onClick={() => toggleDetails(item.id)}
                          className="text-xs text-blue-600 dark:text-blue-400 hover:underline"
                          data-testid={`button-toggle-details-${item.id}`}
                        >
                          {showDetails.has(item.id) ? 'Hide Details' : 'Show Details'}
                        </button>

                        {showDetails.has(item.id) && (
                          <pre className="mt-2 p-2 bg-gray-100 dark:bg-gray-800 rounded text-xs font-mono overflow-x-auto max-h-32">
                            {JSON.stringify(item.data, null, 2)}
                          </pre>
                        )}
                      </div>
                    )}
                  </div>

                  <div className="flex items-center space-x-1 ml-4">
                    <button
                      onClick={() => requeueMutation.mutate(item.id)}
                      disabled={requeueMutation.isPending}
                      className="ecc-button ecc-button--secondary text-xs p-2"
                      data-testid={`button-requeue-${item.id}`}
                    >
                      <RotateCcw className="w-3 h-3" />
                    </button>

                    <button
                      onClick={() => deleteMutation.mutate(item.id)}
                      disabled={deleteMutation.isPending}
                      className="ecc-button ecc-button--ghost text-red-600 hover:text-red-700 text-xs p-2"
                      data-testid={`button-delete-${item.id}`}
                    >
                      <Trash2 className="w-3 h-3" />
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Footer with bulk actions */}
      {filteredItems.length > 0 && (
        <div className="ecc-card__footer">
          <div className="flex items-center justify-between">
            <div className="text-sm text-gray-500">
              {filteredItems.length} items {selectedEntity && `in ${selectedEntity}`}
            </div>

            <div className="flex items-center space-x-2">
              <button
                onClick={() => requeueAllMutation.mutate()}
                disabled={requeueAllMutation.isPending}
                className="ecc-button ecc-button--secondary"
                data-testid="button-requeue-all"
              >
                {requeueAllMutation.isPending ? (
                  <RefreshCw className="w-4 h-4 animate-spin" />
                ) : (
                  <RotateCcw className="w-4 h-4" />
                )}
                Requeue All
              </button>

              <button
                onClick={() => {
                  if (window.confirm(`Are you sure you want to delete all ${filteredItems.length} items? This cannot be undone.`)) {
                    clearAllMutation.mutate();
                  }
                }}
                disabled={clearAllMutation.isPending}
                className="ecc-button ecc-button--error"
                data-testid="button-clear-all"
              >
                {clearAllMutation.isPending ? (
                  <RefreshCw className="w-4 h-4 animate-spin" />
                ) : (
                  <Trash2 className="w-4 h-4" />
                )}
                Clear All
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

import { useState, useEffect, useRef } from "react";
import { Pause, Play, Copy, Trash2, Filter, AlertCircle, Info, Activity, Zap, Download } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface LogEvent {
  id?: string;
  timestamp: number;
  runId?: string;
  entity?: string;
  type: "info" | "warn" | "error" | "progress";
  message: string;
  details?: any;
}

interface LiveLogsProps {
  adminToken: string;
  autoConnect?: boolean;
  maxLogs?: number;
  filters?: {
    entity?: string;
    level?: string;
    runId?: string;
  };
  onFiltersChange?: (filters: { entity?: string; level?: string; runId?: string }) => void;
}

const LOG_TYPE_CONFIG = {
  info: { icon: Info, color: "text-blue-500", bgColor: "bg-blue-50 dark:bg-blue-950", label: "INFO" },
  warn: { icon: AlertCircle, color: "text-yellow-500", bgColor: "bg-yellow-50 dark:bg-yellow-950", label: "WARN" },
  error: { icon: AlertCircle, color: "text-red-500", bgColor: "bg-red-50 dark:bg-red-950", label: "ERROR" },
  progress: { icon: Activity, color: "text-green-500", bgColor: "bg-green-50 dark:bg-green-950", label: "PROGRESS" }
};

export default function LiveLogs({ 
  adminToken, 
  autoConnect = true, 
  maxLogs = 1000,
  filters = {},
  onFiltersChange 
}: LiveLogsProps) {
  const [logs, setLogs] = useState<LogEvent[]>([]);
  const [isConnected, setIsConnected] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [showFilters, setShowFilters] = useState(false);
  const [localFilters, setLocalFilters] = useState(filters);
  const [connectionError, setConnectionError] = useState<string | null>(null);

  const eventSourceRef = useRef<EventSource | null>(null);
  const logsEndRef = useRef<HTMLDivElement>(null);
  const autoScrollEnabled = useRef(!isPaused);
  const { showToast } = useToast();

  // Format timestamp
  const formatTimestamp = (timestamp: number) => {
    return new Date(timestamp).toLocaleTimeString('en-US', { 
      hour12: false,
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit',
      fractionalSecondDigits: 3
    });
  };

  // Filter logs based on current filters
  const filteredLogs = logs.filter(log => {
    if (localFilters.entity && log.entity !== localFilters.entity) return false;
    if (localFilters.level && log.type !== localFilters.level) return false;
    if (localFilters.runId && log.runId !== localFilters.runId) return false;
    return true;
  });

  // Auto-scroll to bottom when new logs arrive
  useEffect(() => {
    if (autoScrollEnabled.current && !isPaused) {
      logsEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    }
  }, [logs, isPaused]);

  // Handle SSE connection
  const connect = () => {
    if (!adminToken) {
      setConnectionError('Admin token is required');
      return;
    }

    disconnect(); // Close any existing connection

    try {
      const eventSource = new EventSource(`/api/admin/sync/logs/stream?token=${encodeURIComponent(adminToken)}`);

      eventSource.onopen = () => {
        setIsConnected(true);
        setConnectionError(null);
        showToast({
          title: "Connected to live logs",
          description: "Real-time log streaming is active",
          variant: "success"
        });
      };

      eventSource.addEventListener('connected', (event) => {
        console.log('SSE Connected:', event.data);
      });

      eventSource.addEventListener('log', (event) => {
        if (isPaused) return;

        try {
          const logEvent: LogEvent = JSON.parse(event.data);

          setLogs(prevLogs => {
            const newLogs = [...prevLogs, logEvent];
            // Keep only the most recent logs to prevent memory issues
            return newLogs.length > maxLogs ? newLogs.slice(-maxLogs) : newLogs;
          });
        } catch (err) {
          console.error('Failed to parse log event:', err);
        }
      });

      eventSource.onerror = (event) => {
        console.error('SSE Error:', event);
        setIsConnected(false);
        setConnectionError('Connection lost - attempting to reconnect...');

        // Auto-reconnect after a delay
        setTimeout(() => {
          if (autoConnect) connect();
        }, 3000);
      };

      eventSourceRef.current = eventSource;
    } catch (err) {
      setConnectionError(`Failed to connect: ${err}`);
      setIsConnected(false);
    }
  };

  const disconnect = () => {
    if (eventSourceRef.current) {
      eventSourceRef.current.close();
      eventSourceRef.current = null;
    }
    setIsConnected(false);
  };

  // Auto-connect on mount if enabled
  useEffect(() => {
    if (autoConnect && adminToken) {
      connect();
    }

    return () => disconnect();
  }, [adminToken, autoConnect]);

  // Handle pause/resume
  const togglePause = () => {
    setIsPaused(!isPaused);
    autoScrollEnabled.current = isPaused; // Will resume auto-scroll when unpausing
  };

  // Clear logs
  const clearLogs = () => {
    setLogs([]);
    showToast({
      title: "Logs cleared",
      description: "All log entries have been removed",
      variant: "success"
    });
  };

  // Copy logs to clipboard
  const copyLogs = async () => {
    const logsText = filteredLogs
      .map(log => `[${formatTimestamp(log.timestamp)}] ${log.type.toUpperCase()}: ${log.message}`)
      .join('\n');

    try {
      await navigator.clipboard.writeText(logsText);
      showToast({
        title: "Logs copied",
        description: `${filteredLogs.length} log entries copied to clipboard`,
        variant: "success"
      });
    } catch (err) {
      showToast({
        title: "Copy failed",
        description: "Failed to copy logs to clipboard",
        variant: "error"
      });
    }
  };

  // Export logs
  const exportLogs = () => {
    const logsText = filteredLogs
      .map(log => `[${formatTimestamp(log.timestamp)}] ${log.type.toUpperCase()}: ${log.message}`)
      .join('\n');

    const blob = new Blob([logsText], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `sync-logs-${new Date().toISOString().slice(0, 19)}.txt`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);

    showToast({
      title: "Logs exported",
      description: `${filteredLogs.length} log entries exported`,
      variant: "success"
    });
  };

  // Apply filters
  const applyFilters = () => {
    onFiltersChange?.(localFilters);
    setShowFilters(false);
  };

  const resetFilters = () => {
    setLocalFilters({});
    onFiltersChange?.({});
  };

  return (
    <div className="ecc-card">
      <div className="ecc-card__header">
        <div className="flex items-center justify-between">
          <h3 className="ecc-card__title flex items-center space-x-2">
            <Zap className="w-5 h-5" />
            <span>Live Logs</span>
            <div className="flex items-center space-x-1">
              <div className={`w-2 h-2 rounded-full ${isConnected ? 'bg-green-400 animate-pulse' : 'bg-red-400'}`} />
              <span className="text-xs text-gray-500">
                {isConnected ? 'Connected' : 'Disconnected'}
              </span>
            </div>
          </h3>

          <div className="flex items-center space-x-2">
            <span className="text-xs text-gray-500">
              {filteredLogs.length} / {logs.length} logs
            </span>

            <button
              onClick={() => setShowFilters(!showFilters)}
              className={`ecc-button ecc-button--ghost p-1 ${showFilters ? 'bg-gray-100 dark:bg-gray-800' : ''}`}
              data-testid="button-toggle-filters"
            >
              <Filter className="w-4 h-4" />
            </button>
          </div>
        </div>

        {/* Filter Panel */}
        {showFilters && (
          <div className="mt-3 p-3 bg-gray-50 dark:bg-gray-800 rounded space-y-3">
            <div className="grid grid-cols-3 gap-3">
              <div>
                <label className="ecc-field-label text-xs">Entity:</label>
                <select
                  value={localFilters.entity || ''}
                  onChange={(e) => setLocalFilters({ ...localFilters, entity: e.target.value || undefined })}
                  className="ecc-input text-xs"
                  data-testid="select-filter-entity"
                >
                  <option value="">All</option>
                  <option value="owners">Owners</option>
                  <option value="properties">Properties</option>
                  <option value="units">Units</option>
                  <option value="leases">Leases</option>
                  <option value="tenants">Tenants</option>
                </select>
              </div>

              <div>
                <label className="ecc-field-label text-xs">Level:</label>
                <select
                  value={localFilters.level || ''}
                  onChange={(e) => setLocalFilters({ ...localFilters, level: e.target.value || undefined })}
                  className="ecc-input text-xs"
                  data-testid="select-filter-level"
                >
                  <option value="">All</option>
                  <option value="info">Info</option>
                  <option value="warn">Warn</option>
                  <option value="error">Error</option>
                  <option value="progress">Progress</option>
                </select>
              </div>

              <div>
                <label className="ecc-field-label text-xs">Run ID:</label>
                <input
                  type="text"
                  value={localFilters.runId || ''}
                  onChange={(e) => setLocalFilters({ ...localFilters, runId: e.target.value || undefined })}
                  placeholder="Filter by run ID"
                  className="ecc-input text-xs"
                  data-testid="input-filter-run-id"
                />
              </div>
            </div>

            <div className="flex space-x-2">
              <button onClick={applyFilters} className="ecc-button ecc-button--primary text-xs" data-testid="button-apply-filters">
                Apply
              </button>
              <button onClick={resetFilters} className="ecc-button ecc-button--secondary text-xs" data-testid="button-reset-filters">
                Reset
              </button>
            </div>
          </div>
        )}

        {/* Connection Error */}
        {connectionError && (
          <div className="mt-2 p-2 bg-red-50 dark:bg-red-950 border border-red-200 dark:border-red-800 rounded">
            <p className="text-sm text-red-600 dark:text-red-400">{connectionError}</p>
          </div>
        )}
      </div>

      <div className="ecc-card__body p-0">
        {/* Logs Container */}
        <div className="h-96 overflow-y-auto bg-gray-900 text-gray-100 font-mono text-sm">
          {filteredLogs.length === 0 ? (
            <div className="flex items-center justify-center h-full text-gray-500">
              {logs.length === 0 ? 'No logs yet...' : 'No logs match current filters'}
            </div>
          ) : (
            <div className="space-y-1 p-2">
              {filteredLogs.map((log, index) => {
                const config = LOG_TYPE_CONFIG[log.type];
                const Icon = config.icon;

                return (
                  <div
                    key={index}
                    className={`flex items-start space-x-2 p-2 rounded text-xs ${config.bgColor.replace('50', '900').replace('950', '800')}`}
                    data-testid={`log-entry-${index}`}
                  >
                    <Icon className={`w-3 h-3 mt-0.5 ${config.color}`} />
                    <span className="text-gray-400 min-w-[80px]">
                      {formatTimestamp(log.timestamp)}
                    </span>
                    <span className={`${config.color} font-semibold min-w-[60px]`}>
                      {config.label}
                    </span>
                    {log.entity && (
                      <span className="text-blue-400 min-w-[60px] capitalize">
                        [{log.entity}]
                      </span>
                    )}
                    {log.runId && (
                      <span className="text-purple-400 min-w-[100px] font-mono text-xs">
                        {log.runId.slice(0, 8)}
                      </span>
                    )}
                    <span className="text-gray-100 flex-1">
                      {log.message}
                    </span>
                  </div>
                );
              })}
              <div ref={logsEndRef} />
            </div>
          )}
        </div>
      </div>

      <div className="ecc-card__footer">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            {!isConnected ? (
              <button
                onClick={connect}
                disabled={!adminToken}
                className="ecc-button ecc-button--primary"
                data-testid="button-connect"
              >
                <Play className="w-4 h-4" />
                Connect
              </button>
            ) : (
              <button
                onClick={disconnect}
                className="ecc-button ecc-button--secondary"
                data-testid="button-disconnect"
              >
                Disconnect
              </button>
            )}

            <button
              onClick={togglePause}
              className={`ecc-button ${isPaused ? 'ecc-button--warning' : 'ecc-button--secondary'}`}
              disabled={!isConnected}
              data-testid="button-toggle-pause"
            >
              {isPaused ? <Play className="w-4 h-4" /> : <Pause className="w-4 h-4" />}
              {isPaused ? 'Resume' : 'Pause'}
            </button>
          </div>

          <div className="flex items-center space-x-2">
            <button
              onClick={copyLogs}
              disabled={filteredLogs.length === 0}
              className="ecc-button ecc-button--ghost"
              data-testid="button-copy-logs"
            >
              <Copy className="w-4 h-4" />
            </button>

            <button
              onClick={exportLogs}
              disabled={filteredLogs.length === 0}
              className="ecc-button ecc-button--ghost"
              data-testid="button-export-logs"
            >
              <Download className="w-4 h-4" />
            </button>

            <button
              onClick={clearLogs}
              disabled={logs.length === 0}
              className="ecc-button ecc-button--ghost text-red-600 hover:text-red-700"
              data-testid="button-clear-logs"
            >
              <Trash2 className="w-4 h-4" />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

