#!/usr/bin/env tsx
import { Pool } from 'pg';
import { writeFileSync } from 'fs';

async function main() {
  console.log('ðŸ”§ Owner Transfer Migration Starting...');

  const dbUrl = process.env.DATABASE_URL || process.env.SUPABASE_DB_URL;
  if (!dbUrl) {
    throw new Error('DATABASE_URL or SUPABASE_DB_URL is required');
  }

  const pool = new Pool({
    connectionString: dbUrl,
    ssl: { rejectUnauthorized: false }
  });

  try {
    await pool.query('BEGIN');
    console.log('ðŸ“¦ Creating owner_transfers table...');

    // Create tables and columns (idempotent)
    const migrationSQL = `
      CREATE TABLE IF NOT EXISTS owner_transfers (
        id BIGSERIAL PRIMARY KEY
      );
      ALTER TABLE owner_transfers
        ADD COLUMN IF NOT EXISTS source_owner_id BIGINT,
        ADD COLUMN IF NOT EXISTS target_owner_id BIGINT,
        ADD COLUMN IF NOT EXISTS effective_date DATE,
        ADD COLUMN IF NOT EXISTS include JSONB,
        ADD COLUMN IF NOT EXISTS notes TEXT,
        ADD COLUMN IF NOT EXISTS status TEXT DEFAULT 'initiated',
        ADD COLUMN IF NOT EXISTS created_at TIMESTAMPTZ DEFAULT now();

      -- make required
      ALTER TABLE owner_transfers
        ALTER COLUMN effective_date SET NOT NULL,
        ALTER COLUMN include SET NOT NULL;

      CREATE TABLE IF NOT EXISTS owner_transfer_audit (
        id BIGSERIAL PRIMARY KEY
      );
      ALTER TABLE owner_transfer_audit
        ADD COLUMN IF NOT EXISTS transfer_id BIGINT,
        ADD COLUMN IF NOT EXISTS action TEXT,
        ADD COLUMN IF NOT EXISTS actor TEXT DEFAULT 'system',
        ADD COLUMN IF NOT EXISTS detail JSONB,
        ADD COLUMN IF NOT EXISTS created_at TIMESTAMPTZ DEFAULT now();
      ALTER TABLE owner_transfer_audit
        ALTER COLUMN action SET NOT NULL;

      -- Add FKs only if targets exist and FK not present
      DO $$
      BEGIN
        IF EXISTS (SELECT 1 FROM information_schema.tables WHERE table_name='owners') THEN
          IF NOT EXISTS (SELECT 1 FROM pg_constraint WHERE conname='owner_transfers_source_owner_id_fkey') THEN
            ALTER TABLE owner_transfers
              ADD CONSTRAINT owner_transfers_source_owner_id_fkey
              FOREIGN KEY (source_owner_id) REFERENCES owners(id);
          END IF;
          IF NOT EXISTS (SELECT 1 FROM pg_constraint WHERE conname='owner_transfers_target_owner_id_fkey') THEN
            ALTER TABLE owner_transfers
              ADD CONSTRAINT owner_transfers_target_owner_id_fkey
              FOREIGN KEY (target_owner_id) REFERENCES owners(id);
          END IF;
        END IF;

        IF NOT EXISTS (SELECT 1 FROM pg_constraint WHERE conname='owner_transfer_audit_transfer_id_fkey') THEN
          ALTER TABLE owner_transfer_audit
            ADD CONSTRAINT owner_transfer_audit_transfer_id_fkey
            FOREIGN KEY (transfer_id) REFERENCES owner_transfers(id);
        END IF;
      END$$;

      CREATE INDEX IF NOT EXISTS idx_owner_transfers_source_owner_id ON owner_transfers(source_owner_id);
      CREATE INDEX IF NOT EXISTS idx_owner_transfers_target_owner_id ON owner_transfers(target_owner_id);
      CREATE INDEX IF NOT EXISTS idx_owner_transfer_audit_transfer_id ON owner_transfer_audit(transfer_id);
    `;

    await pool.query(migrationSQL);
    console.log('âœ… Tables and indexes created');

    // Seeding logic
    const ownersExist = await pool.query(
      "SELECT EXISTS(SELECT 1 FROM information_schema.tables WHERE table_name='owners')"
    );

    if (ownersExist.rows[0].exists) {
      console.log('ðŸŒ± owners table exists, checking for seeding...');

      const transferCount = await pool.query('SELECT COUNT(*) as count FROM owner_transfers');

      if (parseInt(transferCount.rows[0].count) === 0) {
        console.log('ðŸ’¾ Seeding initial transfer...');

        const owners = await pool.query('SELECT id FROM owners LIMIT 2');

        if (owners.rows.length > 0) {
          const sourceId = owners.rows[0].id;
          const targetId = owners.rows.length > 1 ? owners.rows[1].id : owners.rows[0].id;

          const transferResult = await pool.query(`
            INSERT INTO owner_transfers 
            (source_owner_id, target_owner_id, effective_date, include, notes, status)
            VALUES ($1, $2, CURRENT_DATE, '{"properties": true, "units": true}', 'Initial seeded transfer', 'initiated')
            RETURNING id
          `, [sourceId, targetId]);

          const transferId = transferResult.rows[0].id;

          await pool.query(`
            INSERT INTO owner_transfer_audit 
            (transfer_id, action, actor, detail)
            VALUES ($1, 'seeded', 'system', '{"message": "Initial seed data created"}')
          `, [transferId]);

          writeFileSync('tools/.last_transfer_id', transferId.toString());
          console.log(`âœ… Seeded transfer ID: ${transferId}`);
        } else {
          console.log('âš ï¸  No owners found for seeding');
        }
      } else {
        console.log('â„¹ï¸  Transfer data already exists, skipping seed');
      }
    } else {
      console.log('âš ï¸  owners table does not exist, skipping seeding');
    }

    await pool.query('COMMIT');
    console.log('ðŸŽ‰ Migration completed successfully!');

  } catch (error) {
    await pool.query('ROLLBACK');
    console.error('âŒ Migration failed:', error);
    throw error;
  } finally {
    await pool.end();
  }
}

if (import.meta.url === `file://${process.argv[1]}`) {
  main().catch(console.error);
}

src/components/ui/Typeahead.tsx.content:
import { useState, useRef, useEffect } from "react";

interface TypeaheadOption {
  id: string;
  name: string;
  email?: string;
  phone?: string;
}

interface TypeaheadProps {
  value: string;
  onChange: (value: string, option?: TypeaheadOption) => void;
  onSearch: (query: string) => Promise<TypeaheadOption[]>;
  onCreate?: (query: string) => Promise<TypeaheadOption>;
  placeholder?: string;
  label?: string;
  disabled?: boolean;
  testId?: string;
}

export default function Typeahead({
  value,
  onChange,
  onSearch,
  onCreate,
  placeholder,
  label,
  disabled,
  testId
}: TypeaheadProps) {
  const [query, setQuery] = useState(value);
  const [isOpen, setIsOpen] = useState(false);
  const [options, setOptions] = useState<TypeaheadOption[]>([]);
  const [loading, setLoading] = useState(false);
  const [selectedIndex, setSelectedIndex] = useState(-1);
  const inputRef = useRef<HTMLInputElement>(null);
  const listRef = useRef<HTMLUListElement>(null);

  useEffect(() => {
    setQuery(value);
  }, [value]);

  const handleSearch = async (searchQuery: string) => {
    if (!searchQuery.trim()) {
      setOptions([]);
      setIsOpen(false);
      return;
    }

    setLoading(true);
    try {
      const results = await onSearch(searchQuery);
      setOptions(results);
      setIsOpen(true);
      setSelectedIndex(-1);
    } catch (error) {
      console.error("Search failed:", error);
      setOptions([]);
    } finally {
      setLoading(false);
    }
  };

  const handleCreate = async (createQuery: string) => {
    if (!onCreate) return;

    setLoading(true);
    try {
      const newOption = await onCreate(createQuery);
      onChange(newOption.id, newOption);
      setQuery(newOption.name);
      setIsOpen(false);
    } catch (error) {
      console.error("Create failed:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleSelect = (option: TypeaheadOption) => {
    onChange(option.id, option);
    setQuery(option.name);
    setIsOpen(false);
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (!isOpen) return;

    switch (e.key) {
      case "ArrowDown":
        e.preventDefault();
        const maxIndex = options.length + (onCreate ? 1 : 0) - 1;
        setSelectedIndex(prev => Math.min(prev + 1, maxIndex));
        break;
      case "ArrowUp":
        e.preventDefault();
        setSelectedIndex(prev => Math.max(prev - 1, -1));
        break;
      case "Enter":
        e.preventDefault();
        if (selectedIndex === -1) return;
        if (selectedIndex === options.length && onCreate) {
          handleCreate(query);
        } else if (options[selectedIndex]) {
          handleSelect(options[selectedIndex]);
        }
        break;
      case "Escape":
        setIsOpen(false);
        setSelectedIndex(-1);
        break;
    }
  };

  return (
    <div className="relative" data-testid={testId}>
      {label && (
        <label style={{
          display: "block",
          color: "var(--text-primary, #f7f8fb)",
          fontSize: "var(--font-size-sm, 14px)",
          fontWeight: "var(--font-weight-medium, 500)",
          marginBottom: "var(--space-xs, 4px)"
        }}>
          {label}
        </label>
      )}

      <input
        ref={inputRef}
        type="text"
        value={query}
        onChange={(e) => {
          const newQuery = e.target.value;
          setQuery(newQuery);
          handleSearch(newQuery);
        }}
        onKeyDown={handleKeyDown}
        placeholder={placeholder}
        disabled={disabled}
        style={{
          width: "100%",
          padding: "var(--space-sm, 8px) var(--space-md, 12px)",
          backgroundColor: "var(--bg-tertiary, #151821)",
          border: "1px solid var(--border-primary, #1b1f28)",
          borderRadius: "var(--radius-md, 8px)",
          color: "var(--text-primary, #f7f8fb)",
          fontSize: "var(--font-size-md, 14px)",
          outline: "none",
          transition: "border-color 0.2s ease"
        }}
        onFocus={(e) => {
          e.target.style.borderColor = "var(--brand-gold, #f2c86a)";
          if (query.trim()) {
            handleSearch(query);
          }
        }}
        onBlur={(e) => {
          e.target.style.borderColor = "var(--border-primary, #1b1f28)";
          setTimeout(() => setIsOpen(false), 150);
        }}
        data-testid={testId ? `${testId}-input` : undefined}
      />

      {isOpen && (
        <ul
          ref={listRef}
          style={{
            top: "100%",
            left: 0,
            right: 0,
            backgroundColor: "var(--bg-secondary, #111318)",
            border: "1px solid var(--border-primary, #1b1f28)",
            borderRadius: "var(--radius-md, 8px)",
            marginTop: "var(--space-xs, 4px)",
            maxHeight: "200px",
            overflowY: "auto",
            boxShadow: "0 4px 12px rgba(0, 0, 0, 0.5)"
          }}
          data-testid={testId ? `${testId}-options` : undefined}
        >
          {loading ? (
            <li style={{
              padding: "var(--space-sm, 8px) var(--space-md, 12px)",
              color: "var(--text-muted, #8891a3)",
              fontSize: "var(--font-size-sm, 13px)"
            }}>
              Searching...
            </li>
          ) : (
            <>
              {options.map((option, index) => (
                <li
                  key={option.id}
                  onClick={() => handleSelect(option)}
                  style={{
                    padding: "var(--space-sm, 8px) var(--space-md, 12px)",
                    cursor: "pointer",
                    backgroundColor: selectedIndex === index ? "var(--bg-tertiary, #151821)" : "transparent",
                    borderBottom: index < options.length - 1 ? "1px solid var(--border-primary, #1b1f28)" : "none"
                  }}
                  data-testid={testId ? `${testId}-option-${option.id}` : undefined}
                >
                  <div style={{
                    color: "var(--text-primary, #f7f8fb)",
                    fontSize: "var(--font-size-sm, 13px)",
                    fontWeight: "var(--font-weight-medium, 500)"
                  }}>
                    {option.name}
                  </div>
                  {(option.email || option.phone) && (
                    <div style={{
                      color: "var(--text-muted, #8891a3)",
                      fontSize: "var(--font-size-xs, 12px)",
                      marginTop: "var(--space-xs, 4px)"
                    }}>
                      {option.email} {option.phone && `â€¢ ${option.phone}`}
                    </div>
                  )}
                </li>
              ))}

              {options.length === 0 && !loading && onCreate && query.trim() && (
                <li
                  onClick={() => handleCreate(query)}
                  style={{
                    padding: "var(--space-sm, 8px) var(--space-md, 12px)",
                    cursor: "pointer",
                    backgroundColor: selectedIndex === options.length ? "var(--bg-tertiary, #151821)" : "transparent",
                    borderTop: options.length > 0 ? "1px solid var(--border-primary, #1b1f28)" : "none"
                  }}
                  data-testid={testId ? `${testId}-create-option` : undefined}
                >
                  <div style={{
                    color: "var(--brand-gold, #f2c86a)",
                    fontSize: "var(--font-size-sm, 13px)",
                    fontWeight: "var(--font-weight-medium, 500)"
                  }}>
                    Create Owner: {query}
                  </div>
                  <div style={{
                    color: "var(--text-muted, #8891a3)",
                    fontSize: "var(--font-size-xs, 12px)",
                    marginTop: "var(--space-xs, 4px)"
                  }}>
                    Add as new owner
                  </div>
                </li>
              )}
            </>
          )}
        </ul>
      )}
    </div>
  );
}

src/hooks/use-toast.ts.content:
import { useState, useCallback } from "react";

export interface Toast {
  id: number;
  title: string;
  message?: string;
  type?: "info" | "success" | "error" | "warning";
}

let counter = 0;

/**
 * Genesis-grade toast system hook.
 * Keeps local state of toasts and exposes `addToast` + `removeToast`.
 * Wrap UI with your own <ToastContainer /> to render these.
 */
export function useToast() {
  const [toasts, setToasts] = useState<Toast[]>([]);

  const addToast = useCallback((toast: Omit<Toast, "id">) => {
    const id = ++counter;
    setToasts((prev) => [...prev, { id, ...toast }]);
    setTimeout(() => removeToast(id), 5000); // auto-remove after 5s
    return id;
  }, []);

  const removeToast = useCallback((id: number) => {
    setToasts((prev) => prev.filter((t) => t.id !== id));
  }, []);

  return { toasts, addToast, removeToast };
}

