// src/lib/ecc-api-client.ts
export type Json = any;

/** Simplified fetch that expects raw JSON from server */
export async function j<T>(url: string, init?: RequestInit): Promise<T> {
  const r = await fetch(url, init);
  if (!r.ok) throw new Error(`${r.status} ${r.statusText}`);
  return (await r.json()) as T; // server already returns raw JSON
}

/** Money (cents â†’ $X,XXX.xx) */
export function money(cents?: number | null): string {
  const n = Number(cents ?? 0);
  return new Intl.NumberFormat(undefined, { style: "currency", currency: "USD" })
    .format(n / 100);
}

src/styles/ecc.css.content:
:root {
  /* Palette */
  --ecc-bg: #0b0e12;
  --ecc-surface: #1b1f26;          /* app chrome */
  --ecc-object: #232834;           /* light-grey card surface (relative to dark bg) */
  --ecc-border: #2f3542;
  --ecc-border-soft: #2a2f3b;
  --ecc-text: #d7dbe3;
  --ecc-text-dim: #a5adba;
  --ecc-gold: #d6b36a;
  --ecc-green: #35c28d;
  --ecc-red: #f47575;
  --ecc-yellow: #d6b86a;

  /* Spacing + radius */
  --ecc-radius-card: 14px;
  --ecc-radius-btn: 10px;
  --ecc-gap-1: 8px;
  --ecc-gap-2: 12px;
  --ecc-gap-3: 16px;
}

html, body, #root { height: 100%; background: var(--ecc-bg); color: var(--ecc-text); }
* { box-sizing: border-box; }

/* The normalized card surface */
.ecc-object {
  background: var(--ecc-object);
  border: 1px solid var(--ecc-border);
  border-radius: var(--ecc-radius-card);
  box-shadow: 0 1px 0 rgba(0,0,0,0.2), inset 0 1px 0 rgba(255,255,255,0.02);
}

/* Section grammar */
.ecc-section {
  padding: 16px;
}
.ecc-section + .ecc-section { margin-top: 12px; }
.ecc-section__head {
  display: flex; align-items: center; justify-content: space-between;
  margin-bottom: 10px;
}
.ecc-section__title {
  font-weight: 600; letter-spacing: .2px; color: var(--ecc-text);
}
.ecc-section__hint {
  color: var(--ecc-text-dim); font-size: 12px;
}
.ecc-divider { height: 1px; background: var(--ecc-border-soft); margin: 8px 0 12px; }

/* FieldRows baseline (atoms will add variants) */
.ecc-fields { display: grid; grid-template-columns: 180px 1fr; gap: 8px 16px; }
.ecc-label { color: var(--ecc-text-dim); }
.ecc-value { color: var(--ecc-text); }

/* Simple skeleton */
@keyframes ecc-skel { 0%{opacity:.6} 50%{opacity:.3} 100%{opacity:.6} }
.ecc-skel { background: var(--ecc-border-soft); border-radius: 8px; height: 12px; animation: ecc-skel 1.2s ease-in-out infinite; }

src/lib/bff.ts.content:
export type WorkOrderPayload = {
  propertyId: string;
  summary: string;
  priority?: 'low'|'normal'|'high';
};

export type NoticePayload = {
  leaseId: string;
  template: 'renewal' | 'termination' | 'violation' | 'custom';
  customMessage?: string;
};

export type MessagePayload = {
  tenantId: string;
  message: string;
  urgent?: boolean;
};

export type TransferPayload = {
  ownerId: string;
  transferTo: string;
  effective?: string;
};

async function postJson<T>(url: string, body: any): Promise<T> {
  const res = await fetch(url, {
    method: 'POST',
    headers: { 'Content-Type':'application/json' },
    body: JSON.stringify(body),
    credentials: 'include'
  });
  if (!res.ok) throw new Error(`HTTP ${res.status}`);
  return res.json() as Promise<T>;
}

export async function createWorkOrder(input: WorkOrderPayload): Promise<{ ok: boolean; workOrderId?: string; }> {
  return postJson<{ ok: boolean; workOrderId?: string; }>('/api/bff/work-orders/create', input);
}

export async function sendNotice(input: NoticePayload): Promise<{ ok: boolean; noticeId?: string; }> {
  return postJson<{ ok: boolean; noticeId?: string; }>('/api/bff/notices/send', input);
}

export async function sendMessage(input: MessagePayload): Promise<{ ok: boolean; messageId?: string; }> {
  return postJson<{ ok: boolean; messageId?: string; }>('/api/bff/messages/send', input);
}

export async function transferOwnership(input: TransferPayload): Promise<{ ok: boolean; transferId?: string; }> {
  return postJson<{ ok: boolean; transferId?: string; }>('/api/bff/ownership/transfer', input);
}

