// src/shared/card-contracts.ts
import { z } from "zod";

export const Address = z.object({
  line1: z.string().default(""),
  city: z.string().default(""),
  state: z.string().default(""),
  zip: z.string().default(""),
});

export const PropertyCard = z.object({
  property: z.object({
    id: z.number(),
    name: z.string().optional().default(""),
    type: z.string().default("UNKNOWN"),
    address: Address,
  }),
  kpis: z.object({
    units: z.number().default(0),
    activeLeases: z.number().default(0),
    occupancyPct: z.number().default(0),
    avgRentCents: z.number().nullable().default(null),
  }),
});

export const UnitCard = z.object({
  unit: z.object({
    id: z.number(),
    unit_label: z.string().optional().default(""),
    status: z.string().optional().default("UNKNOWN"),
  }),
  property: z.object({ id: z.number() }).nullable().default(null),
  lease: z.object({ id: z.number(), status: z.string().default("UNKNOWN") }).nullable().default(null),
});

export const LeaseCard = z.object({
  lease: z.object({
    id: z.number(),
    status: z.string().default("UNKNOWN"),
    rent_cents: z.number().nullable().default(null),
  }),
  unit: z.object({ id: z.number() }).nullable().default(null),
  tenant: z.object({ id: z.number(), display_name: z.string().default("") }).nullable().default(null),
  property: z.object({ id: z.number() }).nullable().default(null),
});

export const OwnerCard = z.object({
  owner: z.object({
    id: z.number(),
    display_name: z.string().default(""),
  }),
  properties: z.array(z.object({ id: z.number(), name: z.string().default("") })).default([]),
});

export const TenantCard = z.object({
  tenant: z.object({
    id: z.number(),
    display_name: z.string().default(""),
  }),
  leases: z.array(z.object({ id: z.number(), status: z.string().default("UNKNOWN") })).default([]),
  activeLease: z.object({ id: z.number(), status: z.string().default("UNKNOWN") }).nullable().default(null),
});

src/lib/display.ts.content:
export function textOrDash(v: unknown): string {
  return v === null || v === undefined || v === '' ? 'â€”' : String(v);
}
export function numOrDash(v: unknown): string | number {
  return (typeof v === 'number' && Number.isFinite(v)) ? v : 'â€”';
}
export function moneyOrDashCents(cents: unknown): string {
  if (typeof cents === 'number' && Number.isFinite(cents)) {
    return `$${(cents / 100).toLocaleString()}`;
  }
  return 'â€”';
}
export type Addr = { line1?: string | null; city?: string | null; state?: string | null; zip?: string | null };
export function addressLine(addr?: Addr): string {
  if (!addr) return 'â€”';
  const parts: string[] = [];
  if (addr.line1) parts.push(String(addr.line1));
  const tail = [addr.city, addr.state, addr.zip].filter(Boolean).join(' ');
  if (tail) parts.push(tail);
  return parts.join(', ') || 'â€”';
}

src/lib/kpiSafe.ts.content:
export function pct(numerator?: number | null, denominator?: number | null): number | null {
  if (typeof numerator !== "number" || typeof denominator !== "number") return null;
  if (denominator <= 0) return null;
  return Math.round((numerator / denominator) * 100);
}

export function asMoneyCents(cents?: number | null): string | null {
  if (typeof cents !== "number") return null;
  const dollars = cents / 100;
  try {
    return new Intl.NumberFormat(undefined, { style: "currency", currency: "USD", maximumFractionDigits: 0 }).format(dollars);
  } catch {
    return `$${Math.round(dollars)}`;
  }
}

export function asInt(n?: number | null): number | null {
  return typeof n === "number" ? Math.round(n) : null;
}

